// globals
const float delay = 5.;
vec3 rng;
float timeline, glow;

// move brush
vec3 move(float t)
{
    vec2 pos = vec2(0);
    float angle = t*100./delay;
    float radius = .1;
    float jitter = .2;
    float time = t*60./delay;
    float index = floor(time);
    float anim = fract(time);
    float scroll = fract(t/delay);
    vec2 rng = mix(hash21(index), hash21(index+1.), anim);
    pos += (rng*2.-1.)*jitter;
    pos.x += scroll*2.-1.;
    pos.y += pow(abs(sin(time*.2)), 20.)*.4;
    pos.y -= pow(abs(sin(time*.3)), 50.)*.4;
    pos += vec2(cos(angle),sin(angle))*radius;
    return vec3(pos*2., 0);
}

float map(vec3 p)
{
    float dist = 100.;
    float t = timeline;

    // move it
    p -= move(t);

    // twist it
    p.xz *= rot(t*4.);
    p.yz *= rot(t*10.);

    // shape it
    float size = .05 + .03 * sin(t*16.);
    dist = sdBox(p, vec3(.5,2,.5)*size);

    glow += .01/dist;
    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    vec2 uv = fragCoord/R.xy;
    vec2 p = 1.*(fragCoord-R.xy/2.)/R.y;

    // framebuffer
    vec4 frame = texture(iChannel0, uv);

    // accumulate results
    for (float frames = 10.; frames > 0.; --frames)
    {
        // init globals
        float f = float(iFrame) + frames * 200.;
        rng = hash(uvec3(fragCoord, f));
        timeline = rng.x*delay+hash21(floor(iTime/delay)).x*1000.;
        glow = 0.;

        // raymarch
        vec3 pos = vec3(0,0,3);
        vec3 ray = normalize(vec3(p, -1));
        float total = 0.;
        float steps = 0.;
        const float count = 20.;
        for (steps = count; steps > 0.; --steps) {
            float dist = map(pos);
            if (dist < .001 * total || total > max_dist) break;
            total += dist;
            pos += ray * dist;
        }

        // draw if closer
        if (total < max_dist && (total < frame.a || frame.a < .001))
        {
            // data pack
            float shade = steps/count;
            float time = fract(timeline/delay);
            fragColor = vec4(shade, time, glow, total);
            frame = fragColor;
        }
        else
        {
            // store result
            frame.b = max(frame.b, glow);
            fragColor = frame;
        }
    }

    // wipe
    fragColor *= step(.01, fract(iTime/delay));
}

