const int STEPMAX = 32;
const float START = 0.0001;
const float END = 50.001;
const float EPS = 0.00001; 
const int bounces = 4;

struct record{
    float t;
    vec3 p;
    mediump vec3 nor;
    int mat;
};     

struct matInfo{
    vec3 attenuation;
    bool emitted;
};


//https://iquilezles.org/articles/distfunctions/
vec2 sdSphere( vec3 p,vec3 pos, float r,int mat )
{
  return vec2(length(p - pos)-r,mat);
}

vec2 Box(vec3 p,vec3 c,vec3 pos,int mat){
    vec3 p2surf = abs(p - pos) - c;
    return vec2(length(max(p2surf,0.)) + min(max(max(p2surf.x,p2surf.y),p2surf.z),0.),mat);
}


//https://iquilezles.org/articles/distfunctions/
vec2 udQuad( vec3 p, vec3 a, vec3 b, vec3 c, vec3 d ,int mat)
{
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 dc = d - c; vec3 pc = p - c;
  vec3 ad = a - d; vec3 pd = p - d;
  vec3 nor = cross( ba, ad );

  return vec2(sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(dc,nor),pc)) +
     sign(dot(cross(ad,nor),pd))<3.0)
     ?
     min( min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(dc*clamp(dot(dc,pc)/dot2(dc),0.0,1.0)-pc) ),
     dot2(ad*clamp(dot(ad,pd)/dot2(ad),0.0,1.0)-pd) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) )
    ,mat);
}

vec2 map(vec3 p){
    vec2 lightTop = udQuad( p, vec3(-8,3.,0), vec3(-8,3,-10), vec3(8,3,-10), vec3(8,3,0),5);
    vec2 sphere1 =  sdSphere(p,vec3(-2,0.3,-3),1.,2);
    vec2 sphere2 =  sdSphere(p,vec3(0,1,-5),1.,1);
    vec2 lightdown = udQuad( p, vec3(-8,-3.,0), vec3(-8,-3,-10), vec3(8,-3,-10), vec3(8,-3,0),5);
    return uSDF(sphere1,sphere2);
}

float random(float x)
{
    float y = fract(sin(x * iTime)*234.567);
    return y;
}

vec3 randDirInSph(vec3 seed){
    return normalize(vec3(2.*(random(seed.y)-.5),2.*(random(seed.z) -.5), 2.*(random(seed.x)-.5)));
}

vec3 getRay(float fov_degree,vec2 fragCoord,vec2 imgSize){
    vec2 img_center = imgSize / 2.;
    float z_cam = ((imgSize.y/2.)/tan(radians(fov_degree)/2.)); 
    return normalize(vec3(fragCoord,0.) - vec3(img_center,z_cam));
}

vec3 calcNormal( in vec3 p ){ // for function f(p)
    const float h = 0.0003; // replace by an appropriate value
    const vec2 k = vec2(1,-1)*0.5773;
    return normalize( k.xyy*map( p + k.xyy*h ).x + 
                      k.yyx*map( p + k.yyx*h ).x + 
                      k.yxy*map( p + k.yxy*h ).x + 
                      k.xxx*map( p + k.xxx*h ).x );
}    

bool intersection(vec3 ro,vec3 rd,inout record rec){
    float t = START;
    for(int i = 0; i < STEPMAX; i++){
        vec3 p = vec3(ro + t * rd);
        vec2 dis = map(p);
        if(dis.x < EPS){
            rec.p = p;
            rec.t = t;
            rec.mat = int(dis.y);
            rec.nor = calcNormal(rec.p);
            return true;
        }
        t += dis.x;
        if(t > END){
            return false;
        }
    }
    return false;
}    

vec3 getMatAndDir(vec3 seed,int mat,vec3 nor,vec3 inDir,inout matInfo info){
    vec3 dir = vec3(0);
    //mirror
    switch(mat){
        case 1:
        {
            info.attenuation = vec3(.7, .7, .7);
            return reflect(inDir, nor);
            break;
        }
        //red
        case 2:
        {
            info.attenuation = vec3(.65, .05, .05);
            return normalize(nor + randDirInSph(seed));
            break;
        }
        //white
        case 3:
        {
            info.attenuation = vec3(.73, .73, .73);
            return normalize(nor + randDirInSph(seed));
            break;
        }
        //green
        case 4:
        {
            info.attenuation = vec3(.12, .45, .15);
            return normalize(nor + randDirInSph(seed));
            break;
        }
        //light
        case 5:
        {
            info.attenuation = vec3(1,1,1);
            info.emitted = true;
            return normalize(nor + randDirInSph(seed));
            break;
        }
    }
    return dir;
}

vec3 pathTracing(vec3 ro,vec3 rd){
    vec3 r_ori = ro;
    vec3 r_dir = rd;
    vec3 clr = vec3(1,1,1);
    record rec;
    matInfo info;
    for(int i = 0; i < bounces; i++){
        if(intersection(r_ori,r_dir,rec)){
            r_dir = getMatAndDir(rec.p,rec.mat,rec.nor,r_dir,info);
            r_ori = rec.p;
            clr *= info.attenuation;
            if(info.emitted){
                return clr;
            }
        }
    }
    return texture(iChannel1,r_dir).rgb * clr;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    //AA
    vec2 jitter = randDirInSph(fragCoord.xyy).xz/2.;
    vec3 camPos =vec3(0,0,10);
    vec3 dir = getRay(45.0,fragCoord+jitter,iResolution.xy);  
    vec4 clr =vec4(pathTracing(camPos,dir),1.);
    vec4 lastFrame;
    if(iFrame == 0){
        fragColor = clr;
        return;
    }else{
        lastFrame = texture(iChannel0,fragCoord/iResolution.xy);
    }
    fragColor = mix(lastFrame,clr,1./ float(iFrame+1));
}

