// https://iquilezles.org/articles/distfunctions2d/
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdCircle(vec2 p, float r){
    return length(p)-r;
}

// Ref: "Foundation ActionScript 3.0 Animation" P.367
vec2 ik(vec2 A, vec2 C, float a, float b){
    vec2 oC = C-A;
    float aD = atan(oC.y, oC.x);
    float c = min(a+b, (C.x-A.x)/cos(aD));
    if(c<=abs(a-b))return normalize(C)*a;
    float aB = acos((b*b - a*a - c*c) / (-2.*a*c));
    float aDB = -(aD+aB)+PI*.5;
    return vec2(sin(aDB),cos(aDB))*a;
}

void mainImage( out vec4 O, in vec2 R )
{
    vec2 uv = ( R - .5 * iResolution.xy ) / iResolution.y;

    const float l1 = .5;
    const float l2 = .3;
    vec2 st = vec2(0);//vec2(sin(iTime*3.),cos(iTime*3.))*.01;
    vec2 ed = vec2(sin(iTime),cos(iTime))*(.4+(sin(iTime*5.)+1.)*.2);
    if(iMouse.z>0.)ed = -vec2((iMouse.x/iResolution.x-.5)*iResolution.x/iResolution.y, iMouse.y/iResolution.y-.5);

    vec2 md = ik(st, ed, l1, l2);

    float s = sdCircle(uv+st, .025);
    float m = sdCircle(uv+st+md, .025);
    float e = sdCircle(uv+st+ed, .025);
    float seg1 = sdSegment(uv, -st, -st-md);
    float seg2 = sdSegment(uv, -st-md, -st-ed);

    float cirStMd = abs(sdCircle(uv+st, l1))-.001;
    float cirMdEd = abs(sdCircle(uv+st+md, l2))-.001;

    float aas = 2.5/iResolution.y;
    float d = smoothstep(.0,aas,min(s,min(m,e)));
    d = smoothstep(.0,aas,min(d,min(seg1,seg2)));
    d = min(d, smoothstep(.0,aas,cirStMd));
    d = min(d, smoothstep(.0,aas,cirMdEd));
    vec3 col = mix(vec3(0.875,0.835,0.749), vec3(0.102,0.082,0.039), d);    
    O = vec4(col,1.0);
}

