const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qt;
  float dMin, d, h;
  dMin = dstFar;
  h = 4. * flSize + 1.;
  q = p;
  q.y -= h + bsSize.y;
  d = PrCylDf (q.xzy, 0.1, h);
  DMINQ (1);
  q = p;
  q.y = abs (q.y - h - bsSize.y) - h;
  d = PrCylDf (q.xzy, 0.2, 0.06);
  DMINQ (2);
  q = p;
  d = max (PrRoundBoxDf (q, bsSize.xyz - bsSize.w, bsSize.w), - q.y);
  DMINQ (3);
  qt.xz = abs (p.xz) + 0.4;
  qt.y = p.y - 0.7;
  qt -= bsSize.xyz;
  d = min (PrCylDf (vec3 (qt.xy, p.z), 0.1, bsSize.z - 0.4),
     PrCylDf (vec3 (p.x, qt.yz).zyx, 0.1, bsSize.x - 0.4));
  qt.y -= -0.3;
  d = min (d, PrCylDf (qt.xzy, 0.1, 0.4));
  DMINQ (4);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 GetR (vec2 v)
{
  return Loadv4 (v).xyz;
}

float SheetRay (vec3 ro, vec3 rd)
{
  vec3 r, q;
  vec2 g;
  float d, dMin, sz, szMax, szMin, grLen;
  bvec2 ilt;
  bool bkTrak;
  grLen = 0.92 * spLen;
  dMin = dstFar / (grLen * szFac);
  ro /= szFac;
  rd /= grLen;
  szMax = float (nBallE.x - 1);
  szMin = 1./32.;
  sz = szMax;
  g = vec2 (0.);
  bkTrak = false;
  for (int ns = VAR_ZERO; ns < nsMax; ns ++) {
    if (sz == szMin) {
      r = GetR (g) - ro;
      q = cross (r, rd);
      if (dot (q, q) < sz * sz) {
        d = dot (r, rd);
        if (d < dMin) {
          dMin = d;
          qgHit = g;
        }
      }
      bkTrak = true;
    }
    q = cross (GetR (g + 0.5 * sz) - ro, rd);
    bkTrak = bkTrak || (dot (q, q) > sz * sz);
    if (bkTrak) {
      bkTrak = false;
      ilt = lessThan (fract (g / (2. * sz)), vec2 (0.5));
      g.x += ilt.x ? sz : - sz;
      if (! ilt.x) {
        if (sz == 0.5 * szMax) break;
        g.y += ilt.y ? sz : - sz;
        if (! ilt.y) {
          sz *= 2.;
          if (sz == szMax) break;
          bkTrak = true;
        }
      }
    } else if (sz > szMin) sz *= 0.5;
  }
  return dMin * grLen * szFac;
}

vec3 GetRC (vec2 v)
{
  return GetR (clamp (v, vec2 (0.), vec2 (nBallE - 1)));
}

vec3 SheetNf () 
{
  vec3 c;
  vec2 e = vec2 (1., 0.);
  c = GetRC (qgHit);
  return normalize (cross (GetRC (qgHit + e.yx) - c, GetRC (qgHit + e) - c));
}

float SheetSilHit (vec3 ro, vec3 rd)
{
  vec3 blkSize, pMin, pMax, v, tm, tp;
  float dn, df;
  pMin = Loadv4 (vec2 (1, nBallE.y)).xyz;
  pMax = Loadv4 (vec2 (2, nBallE.y)).xyz;
  ro /= szFac;
  ro -= 0.5 * (pMin + pMax);
  blkSize = 0.5 * (pMax - pMin) + 0.5;
  v = ro  / rd;
  tp = blkSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  return (df > 0. && dn < df) ? szFac * dn : dstFar;
}

vec3 SheetCol (float s)
{  // specs from Wikipedia
  vec3 col, colB, colR, colW;
  vec2 p, q;
  float w;
  p = (qgHit / (0.5 * float (nBallE.y - 1)) - 1.);
  p.x -= 1.;
  colB = vec3 (1., 33., 105.) / 255.;
  colR = vec3 (200., 16., 46.) / 255.;
  colW = vec3 (1.);
  col = colB;
  w = 1./15.;
  q = Rot2D (p, atan (0.5));
  col = mix (colW, col, smoothstep (0., 0.02, abs (q.y) - 3. * w));
  col = mix (colR, col, smoothstep (0., 0.02, abs (q.y - sign (p.x) * w) - w));
