  spLenD = spLen * sqrt(2.);
  for (int n = 0; n < 4; n ++) {
    ivn = iv + idNebD[n];
    if (ivn.y >= 0 && ivn.y < nBallE.y && ivn.x >= 0 && ivn.x < nBallE.x) {
      dr = r - GetR (vec2 (ivn));
      f += fSpringD * (spLenD - length (dr)) * normalize (dr) -
         fDamp * (v - GetV (vec2 (ivn)));
    }
  }
  return f;
}

vec3 BendForce (ivec2 iv, vec3 r)
{
  vec3 dr1, dr2, rt, f;
  ivec2 ivd, ivp, ivm;
  float s, c11, c22, c12, cd;
  bool doInt;
  f = vec3 (0.);
  for (int nd = 0; nd < 2; nd ++) {
    ivd = (nd == 0) ? ivec2 (1, 0) : ivec2 (0, 1);
    for (int k = 0; k < 3; k ++) {
      doInt = false;
      if (nd == 0) {
        if (k == 0 && iv.x > 1) {
          ivp = iv - ivd;
          ivm = ivp - ivd;
          doInt = true;
        } else if (k == 2 && iv.x < nBallE.x - 2) {
          ivm = iv + ivd;
          ivp = ivm + ivd;
          doInt = true;
        } else if (k == 1 && (iv.x > 0 && iv.x < nBallE.x - 1)) {
          ivp = iv + ivd;
          ivm = iv - ivd;
          doInt = true;
        }
      } else {
        if (k == 0 && iv.y > 1) {
          ivp = iv - ivd;
          ivm = ivp - ivd;
          doInt = true;
        } else if (k == 2 && iv.y < nBallE.y - 2) {
          ivm = iv + ivd;
          ivp = ivm + ivd;
          doInt = true;
        } else if (k == 1 && (iv.y > 0 && iv.y < nBallE.y - 1)) {
          ivp = iv + ivd;
          ivm = iv - ivd;
          doInt = true;
        }
      }
      if (doInt) {
        if (k == 0) {
          rt = GetR (vec2 (ivp));
          dr1 = rt - GetR (vec2 (ivm));
          dr2 = r - rt;
          s = -1.;
        } else if (k == 2) {
          rt = GetR (vec2 (ivm));
          dr1 = rt - r;
          dr2 = GetR (vec2 (ivp)) - rt;
          s = -1.;
        } else {
          dr1 = r - GetR (vec2 (ivm));
          dr2 = GetR (vec2 (ivp)) - r;
          s = 1.;
        }
        c11 = 1. / dot (dr1, dr1);
        c12 = dot (dr1, dr2);
        c22 = 1. / dot (dr2, dr2);
        cd = sqrt (c11 * c22);
        s *= cd * (c12 * cd - 1.);
        if (k <= 1) f += s * (dr1 - c12 * c22 * dr2);
        if (k >= 1) f += s * (c12 * c11 * dr1 - dr2);
      }
    }
  }
  return f;
}

vec3 PairForce (vec3 r)
{
  vec3 dr, f;
  float rSep;
  int nx, ny;
  f = vec3 (0.);
  nx = 0;
  ny = 0;
  for (int n = 0; n < nBallE.x * nBallE.y; n ++) {
    dr = r - GetR (vec2 (nx, ny));
    rSep = length (dr);
    if (rSep > 0.01 && rSep < 1.) f += (1. / rSep - 1.) * dr;
    if (++ nx == nBallE.x) {
      nx = 0;
      ++ ny;
    }  
  }
  return f;
}

vec3 NormForce (ivec2 iv, vec3 r, vec3 v)
{
  vec3 f, n;
  ivec2 e = ivec2 (1, 0);
  f = vec3 (0.);
  if (iv.y > 0 && iv.y < nBallE.y - 1 && iv.x > 0 && iv.x < nBallE.x - 1) {
    n = normalize (cross (GetR (vec2 (iv + e.yx)) - GetR (vec2 (iv - e.yx)),
       GetR (vec2 (iv + e)) - GetR (vec2 (iv - e))));
    f = - dot (v, n) * n;
  }
  return f;
}

void IdNebs ()
{
  idNeb[0] = ivec2 (1, 0);
  idNeb[1] = - idNeb[0];
  idNeb[2] = ivec2 (0, 1);
  idNeb[3] = - idNeb[2];
  idNebD[0] = ivec2 (1, 1);
  idNebD[1] = - idNebD[0];
  idNebD[2] = ivec2 (1, -1);
  idNebD[3] = - idNebD[2];
}

void Step (ivec2 iv, out vec3 r, out vec3 v)
{
  vec3 dr, f, wDir;
  vec2 s;
  float fOvlap, fBend, fGrav, fDamp, dt;
  IdNebs ();
  fOvlap = 1000.;
  fBend = 50.;
  fGrav = 0.25;
  fDamp = 0.5;
  wDir = vec3 (Rot2D (vec2 (-1., 0.), 0.7 * pi * (2. * Fbm1 (1.5 * tCur) - 1.)), 0.).xzy;
  r = GetR (vec2 (iv));
  v = GetV (vec2 (iv));
  f = fOvlap * PairForce (r) + SpringForce (iv, r, v) + fBend * BendForce (iv, r) +
     (1. + 10. * Fbm1 (tCur)) * NormForce (iv, r, wDir);
  f -= fDamp * v;
  f.y -= fGrav;
  dt = 0.02;
  if (iv.x != 0 || mod (float (iv.y), float ((nBallE.y - 1) / 8)) != 0. && iv.y != nBallE.y - 2) {
    v += dt * f;
    r += dt * v;
  }
}

void Init (ivec2 iv, out vec3 r, out vec3 v)
{
  float fn;
  for (int iy = 0; iy < nBallE.y; iy ++) {
    for (int ix = 0; ix < nBallE.x; ix ++) {
      if (iv.x == ix && iv.y == iy) {
        r = vec3 (ix, iy, 0.);
        r.xy -= 0.5 * (vec2 (nBallE.x, nBallE.y) - 1.);
        r *= 0.97 * spLen;
      }
    }
  }
  fn = float (iv.y * nBallE.x + iv.x);
  v = 1. * normalize (vec3 (Hashff (fn), Hashff (fn + 0.3), Hashff (fn + 0.6)) - 0.5);
}

void SheetLims ()
{
  vec3 p;
  pMin = vec3 (1e6);
  pMax = - pMin;
  for (int iy = 0; iy < nBallE.y; iy ++) {
    for (int ix = 0; ix < nBallE.x; ix ++) {
      p = GetR (vec2 (ix, iy));
      pMin = min (pMin, p);
      pMax = max (pMax, p);
    }
  }
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat;
  vec3 r, v;
  ivec2 pxIv, iv;
  int mId, pxId, nb;
  tCur = iTime;
  pxIv = ivec2 (fragCoord);
  pxId = pxIv.x + 2 * nBallE.x * pxIv.y;
  nb = nBallE.x * nBallE.y;
  if (pxIv.x >= 2 * nBallE.x || pxId >= 2 * nb + 3) discard;
  stDat = Loadv4 (vec2 (2, nBallE));
  iv = pxIv;
  if (iv.x >= nBallE.x) iv.x -= nBallE.x;
  mId = iv.y * nBallE.x + iv.x;
  if (iFrame <= 2) {
    spLen = 1.1;
    nStep = 0.;
    if (mId < nb) Init (iv, r, v);
  } else {
    stDat = Loadv4 (vec2 (0, nBallE.y));
    spLen = stDat.x;
    nStep = stDat.y;
    stDat = Loadv4 (vec2 (3, nBallE.y));
    ++ nStep;
    if (mId < nb) Step (iv, r, v);
  }
  if (pxId == 2 * nb + 1 || pxId == 2 * nb + 2) SheetLims ();
  if (pxId < 2 * nb) stDat = vec4 (((pxIv.x >= nBallE.x) ? v : r), 0.);
  else {
    if      (pxId == 2 * nb + 0) stDat = stDat = vec4 (spLen, nStep, 0., 0.);
    else if (pxId == 2 * nb + 1) stDat = vec4 (pMin, 0.);
    else if (pxId == 2 * nb + 2) stDat = vec4 (pMax, 0.);
  }
  Savev4 (vec2 (pxIv), stDat, fragColor, fragCoord);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (vec2 vId)
{
  return texture (txBuf, (vId + 0.5) / txSize);
}

void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  d = abs (fCoord - vId - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

