//
vec3 CameraRay_Perspective(vec3 eyePos,vec3 upVec,vec3 front,float near,vec2 size,vec2 uv)
{
    upVec=normalize(upVec);
    front=normalize(front);
    vec3 right=cross(front,upVec);
    vec3 hitPoint=vec3(size*uv,0);
    hitPoint=hitPoint.x*right+hitPoint.y*upVec+eyePos+front*near;
    return normalize(hitPoint-eyePos);
}

bool LightCollision(vec3 eyePos,vec3 rayDir,out HitInfo hitInfo)
{
    hitInfo=HitInfo(false,vec3(0,0,0),LIGHT_MAX_DIST,white_material,vec3(0,0,0));

    Scene(eyePos,rayDir,hitInfo);
    Light(eyePos,rayDir,hitInfo);
    if(hitInfo.dist!=LIGHT_MAX_DIST)
    {
        return true;
    }
    else
    {
        return false;
    }
} 

uint random_seed;

vec4 PathTracing(vec3 rayPoint,vec3 rayDir)
{
    //
    vec3 result=vec3(0,0,0);
    //
    vec3 intensity=vec3(1,1,1);

    vec3 pos_now=rayPoint;
    vec3 dir_now=rayDir;

    for(int i=0;i<BOUNCE_CNT;i++)
    {
        HitInfo hitInfo;
        if(!LightCollision(pos_now,dir_now,hitInfo))
        {
            result+=SRGBToLinear(texture(iChannel1,dir_now).rgb)*intensity;
            break;
        }

        //
        /*if(hitInfo.inside)
        {
            intensity*=exp(-hitInfo.material.refractionColor*hitInfo.dist);
        }*/

        //specular-roughness
        float specularChance=hitInfo.material.specularChance;
        float refractionChance=hitInfo.material.refractionChance;

        //Fresnel
        if (specularChance > 0.0f)
        {
            specularChance = FresnelReflectAmount(
                hitInfo.inside?hitInfo.material.IOR:1.0f,
                !hitInfo.inside?hitInfo.material.IOR:1.0f,
                dir_now, hitInfo.normal, hitInfo.material.specularChance, 1.0f);

            float ratio=(1.0f-specularChance)/(1.0f-hitInfo.material.specularChance);
            refractionChance*=ratio;
        }

        //
        float rayProbability;
        float doSpecular=0.0f;
        float doRefraction=0.0f;
        float rnd=RandomFloat01(random_seed);
        if(rnd<specularChance&&specularChance>0.0f)
        {
            doSpecular=1.0f;
            rayProbability=specularChance;
        }
        else if(rnd<specularChance+refractionChance&&refractionChance>0.0f)
        {
            doRefraction=1.0f;
            rayProbability=refractionChance;
        }
        else
        {
            rayProbability=1.0f-specularChance-refractionChance;
        }

        rayProbability=max(rayProbability,0.001f);

        //
        if(doRefraction>0.0f)
        {
            pos_now=pos_now+dir_now*hitInfo.dist-hitInfo.normal*rayPosNormalNudge;
        }
        else
        {
            pos_now=pos_now+dir_now*hitInfo.dist+hitInfo.normal*rayPosNormalNudge;
        }

        vec3 diffuseDir=normalize(hitInfo.normal+RandomUnitVector(random_seed));

        vec3 specularDir=reflect(dir_now,hitInfo.normal);
        specularDir=normalize(mix(specularDir,diffuseDir,hitInfo.material.specularRoughness*hitInfo.material.specularRoughness));

        vec3 refractionDir = refract(dir_now,hitInfo.inside? -hitInfo.normal:hitInfo.normal, hitInfo.inside ? hitInfo.material.IOR : 1.0f / hitInfo.material.IOR);
        refractionDir = normalize(mix(refractionDir, normalize(-hitInfo.normal + RandomUnitVector(random_seed)), hitInfo.material.refractionRoughness*hitInfo.material.refractionRoughness));

        dir_now = mix(diffuseDir, specularDir, doSpecular);
        dir_now = mix(dir_now, refractionDir, doRefraction);

        //
        result+=hitInfo.material.emissive*intensity;

        //
        if (doRefraction == 0.0f)
            intensity *= mix(hitInfo.material.albedo, hitInfo.material.specularColor, doSpecular);

        intensity/=rayProbability;

        //Russian-Roulette
        float p=max(intensity.r,max(intensity.g,intensity.b));
        if(RandomFloat01(random_seed)>sqrt(p))
        {
            break;
        }
        intensity*=(1.0/p);
    }

    return vec4(result,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    random_seed=uint(uint(fragCoord.x) * uint(1973) + uint(fragCoord.y) * uint(9277) + uint(iFrame) * uint(26699)) | uint(1);
