const float PI=3.1415926526;

mat4 translate(vec3 delta)
{
	return mat4(
		vec4(1,0,0,0),
		vec4(0,1,0,0),
		vec4(0,0,1,0),
		vec4(delta.x,delta.y,delta.z,1));
}

mat4 scale(vec3 delta)
{
	return mat4(
		vec4(delta.x,0,0,0),
		vec4(0,delta.y,0,0),
		vec4(0,0,delta.z,0),
		vec4(0,0,0,1));
}

mat4 rotate(vec3 axis,float theta_rad)
{
	axis=normalize(axis);
	float cos_theta=cos(theta_rad);
	float sin_theta=sin(theta_rad);
	return mat4(
		vec4(axis.x*axis.x*(1.0-cos_theta)+cos_theta       , axis.y*axis.x*(1.0-cos_theta)+axis.z*sin_theta, axis.z*axis.x*(1.0-cos_theta)-axis.y*sin_theta , 0.0),
		vec4(axis.x*axis.y*(1.0-cos_theta)-axis.z*sin_theta, axis.y*axis.y*(1.0-cos_theta)+cos_theta       , axis.z*axis.y*(1.0-cos_theta)+axis.x*sin_theta, 0.0),
		vec4(axis.x*axis.z*(1.0-cos_theta)+axis.y*sin_theta, axis.y*axis.z*(1.0-cos_theta)-axis.x*sin_theta, axis.z*axis.z*(1.0-cos_theta)+cos_theta       , 0.0),
		vec4(0.0                                           , 0.0                                           , 0.0                                           , 1.0));
}

uint wang_hash(inout uint seed)
{
    seed = uint(seed ^ uint(61)) ^ uint(seed >> uint(16));
    seed *= uint(9);
    seed = seed ^ (seed >> 4);
    seed *= uint(0x27d4eb2d);
    seed = seed ^ (seed >> 15);
    return seed;
}

float RandomFloat01(inout uint state)
{
    return float(wang_hash(state)) / 4294967296.0;
}

vec3 RandomUnitVector(inout uint state)
{
    float z = RandomFloat01(state) * 2.0f - 1.0f;
    float a = RandomFloat01(state) * 2.0f*PI;
    float r = sqrt(1.0f - z * z);
    float x = r * cos(a);
    float y = r * sin(a);
    return vec3(x, y, z);
}

vec3 LessThan(vec3 f, float value)
{
    return vec3(
        (f.x < value) ? 1.0f : 0.0f,
        (f.y < value) ? 1.0f : 0.0f,
        (f.z < value) ? 1.0f : 0.0f);
}

vec3 LinearToSRGB(vec3 rgb)
{
    rgb = clamp(rgb, 0.0f, 1.0f);

    return mix(
        pow(rgb, vec3(1.0f / 2.4f)) * 1.055f - 0.055f,
        rgb * 12.92f,
        LessThan(rgb, 0.0031308f)
    );
}

vec3 SRGBToLinear(vec3 rgb)
{
    rgb = clamp(rgb, 0.0f, 1.0f);

    return mix(
        pow(((rgb + 0.055f) / 1.055f), vec3(2.4f)),
        rgb / 12.92f,
        LessThan(rgb, 0.04045f)
    );
}

vec3 ACESFilm(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x + b)) / (x*(c*x + d) + e), 0.0f, 1.0f);
}

//,
float DistPoint2Line(vec3 point,vec3 rayPos,vec3 dir)
{
    return length(cross(point-rayPos,dir));
}

//
float FresnelReflectAmount(float n1, float n2, vec3 normal, vec3 incident, float f0, float f90)
{
    // Schlick aproximation
    float r0 = (n1-n2) / (n1+n2);
    r0 *= r0;
    float cosX = -dot(normal, incident);
    if (n1 > n2)
    {
        float n = n1/n2;
        float sinT2 = n*n*(1.0-cosX*cosX);
        // Total internal reflection
        if (sinT2 > 1.0)
            return f90;
        cosX = sqrt(1.0-sinT2);
    }
    float x = 1.0-cosX;
    float ret = r0+(1.0-r0)*x*x*x*x*x;
    // adjust reflect multiplier for object reflectivity
    return mix(f0, f90, ret);
}


const float LIGHT_MAX_DIST=10000.0f;

//
float Sphere(vec3 rayPos,vec3 rayDir,float radius,out vec3 normal,out bool fromInside)
{
	float dist_point2line=DistPoint2Line(vec3(0,0,0),rayPos,rayDir);
	float dist_points=length(rayPos);

	bool ray_facesphere=dot(rayDir,-rayPos)>0.0;
	bool line_close=dist_point2line<radius;
	bool point_close=dist_points<radius;

	if(!point_close&&((!line_close&&ray_facesphere)||!ray_facesphere))
	{
		fromInside=false;
		return LIGHT_MAX_DIST;
	}
	else
	{
		float A=1.0f;
		float B=2.0f*dot(rayDir,rayPos);
		float len_O=length(rayPos);
		float C=(len_O+radius)*(len_O-radius);

		float sqrt_delta=sqrt(B*B-4.0*A*C);
		float t1=(-B+sqrt_delta)/(2.0f*A);
		float t2=(-B-sqrt_delta)/(2.0f*A);

		float res=LIGHT_MAX_DIST;

		if(t1>.0)
		{
			res=min(res,t1);
		}
		if(t2>.0)
		{
			res=min(res,t2);
		}

		fromInside=point_close;
		normal=normalize(rayPos+rayDir*res);
		return res;
	}
}

//(Moller-Trumbore)
float Triangle(vec3 rayPos,vec3 rayDir,vec3 a,vec3 b,vec3 c,out vec3 normal)
{
	vec3 E1=b-a;
	vec3 E2=c-a;
	vec3 S=rayPos-a;
	vec3 S1=cross(rayDir,E2);
	vec3 S2=cross(S,E1);

	float S1E1=dot(S1,E1);
	float b1_S1E1=dot(S1,S);
	float b2_S1E1=dot(S2,rayDir);
	if(b1_S1E1>.0&&b2_S1E1>.0&&S1E1-b1_S1E1-b2_S1E1>.0)
	{
		normal=normalize(cross(E1,E2));
		return dot(S2,E2)/S1E1;
	}
	else
	{
		return LIGHT_MAX_DIST;
	}
}

float Quad(vec3 rayPos,vec3 rayDir,vec3 a,vec3 b,vec3 c,vec3 d,out vec3 normal)
{
	float res=LIGHT_MAX_DIST;
	vec3 normal1,normal2;
	float res1=Triangle(rayPos,rayDir,a,b,d,normal1);
	float res2=Triangle(rayPos,rayDir,d,b,c,normal2);
	if(res1<res2)
	{
		normal=normal1;
	}
	else
	{
		normal=normal2;
	}
	return min(res,min(res1,res2));
}

float Box(vec3 rayPos,vec3 rayDir,vec3 center,vec3 size,out vec3 normal,out bool fromInside)
{
	float res=LIGHT_MAX_DIST;

	size-=(size/2.0f);
	vec3 p1=center+size;
	vec3 p2=center+vec3(-size.x,size.y,size.z);
	vec3 p3=center+vec3(-size.x,-size.y,size.z);
	vec3 p4=center+vec3(size.x,-size.y,size.z);
	vec3 p5=center+vec3(size.x,size.y,-size.z);
	vec3 p6=center+vec3(-size.x,size.y,-size.z);
	vec3 p7=center+vec3(-size.x,-size.y,-size.z);
	vec3 p8=center+vec3(size.x,-size.y,-size.z);

	vec3 n1,n2,n3,n4,n5,n6;
	float r1=Quad(rayPos,rayDir,p1,p2,p3,p4,n1);
	if(r1<res)
	{
		res=r1;
		normal=n1;
	}
	float r2=Quad(rayPos,rayDir,p1,p5,p6,p2,n2);
	if(r2<res)
	{
		res=r2;
		normal=n2;
	}
	float r3=Quad(rayPos,rayDir,p4,p8,p7,p3,n3);
	if(r3<res)
	{
		res=r3;
		normal=n3;
	}
	float r4=Quad(rayPos,rayDir,p1,p4,p8,p5,n4);
	if(r4<res)
	{
		res=r4;
		normal=n4;
	}
	float r5=Quad(rayPos,rayDir,p3,p2,p6,p7,n5);
	if(r5<res)
	{
		res=r5;
		normal=n5;
	}

	float r6=Quad(rayPos,rayDir,p5,p8,p7,p6,n6);
	if(r6<res)
	{
		res=r6;
		normal=n6;
	}

	vec3 relativePos=rayPos-center;
	fromInside=abs(relativePos.x)<size.x&&abs(relativePos.y)<size.y&&abs(relativePos.z)<size.z;
	return res;
}

