const float D_MAX = 4.0;
const float BASE_FREQUENCY = 1.0 / 60.0;
const float PI = 3.14159265;
const float POINT_COUNT = 60.0;
const float MARGIN = .2; //percent margin on shortest side

const vec4 BLACK = vec4(0,0,0,0);
const vec4 WHITE = vec4(1,1,1,0);
const vec4 GRAY = vec4(1,1,1,0) * .6;
const vec4 RED = vec4(1,0,0,0);
const vec4 GREEN = vec4(0,1,0,0);
const vec4 BLUE = vec4(0,0,1,0);

vec4 alpha_blend(vec4 top, vec4 bottom, float alpha){
    return mix(bottom,top,clamp(alpha,0.0,1.0));
}

float sdf_to_alpha(float d){
    float scale_divisor = min(iResolution.x, iResolution.y)/2.0*(1.0-MARGIN);
    return clamp(-d*scale_divisor,0.0,1.0);
}

float point_sdf(float t, vec2 xy0, float multiplier){
    // t is time of day in seconds
    // xy0 is from -1 to 1
    //returns a distance to the point with frequency multiplier

    float t2 = fract(t * BASE_FREQUENCY) * multiplier;
    //rotate clockwise from 12:00
    float theta = PI/2.0 - fract(t2) * 2.0 * PI;

    vec2 xy = multiplier/POINT_COUNT * vec2(cos(theta), sin(theta)) * float (multiplier <= POINT_COUNT);
    return length(xy-xy0);
}

float hand(float t, float period, vec2 xy0, float thickness, float height){
    //t is time of day in seconds
    //xy0 is from -1 to 1
    //thickness from 0 to 1
    //height from 0 to 1
    float angle = fract(t / period) * 2.0 * PI;
    float back_height = 0.2 * height;
    //rotate xy0 angle by angle
    float r = length(xy0);
    float theta = atan(xy0.y, xy0.x) + angle;
    vec2 xy1 = r * vec2(cos(theta), sin(theta));
    //bounding box sdf
    float dx = abs(xy1.x)-thickness/2.0;
    float dy = max(xy1.y-height,-xy1.y-back_height);
    float sdf = max(dx,dy);
    return sdf_to_alpha(sdf);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale_divisor = min(iResolution.x, iResolution.y)/2.0*(1.0-MARGIN);
    vec2 xy0 = (fragCoord - iResolution.xy/2.0)/scale_divisor;

    scale_divisor = 1.0;
    float frequency = max(floor(length(xy0)*POINT_COUNT), 1.0);

    float t = mod(iDate.w, 12.0*60.0*60.0);
    //t = iDate.w;
    //not a true sdf. only valid at the boundry
    float blobs_sdf = 0.0;
    blobs_sdf = 999.0;
    for(float i = -D_MAX; i<=D_MAX; i++){
        float r = max(frequency+i, 1.0);
        float dist = point_sdf(t, xy0, r);
        blobs_sdf = min(blobs_sdf, dist);
    }
    float blobs_alpha = sdf_to_alpha(blobs_sdf-D_MAX/POINT_COUNT);

    t = floor(t); // comment out for smooth
    //background
    fragColor = vec4(.95,.9,.8,0);
    fragColor = alpha_blend(BLACK, fragColor, sdf_to_alpha(length(xy0)-1.15));
    fragColor = alpha_blend(WHITE, fragColor, sdf_to_alpha(length(xy0)-1.1));
    // points ... hours, minutes, seconds
    fragColor = alpha_blend(GRAY,  fragColor, blobs_alpha);
    fragColor = alpha_blend(BLACK, fragColor, hand(t, 12.0*60.0*60.0, xy0, .05, .7));
    fragColor = alpha_blend(BLACK, fragColor, hand(t,      60.0*60.0, xy0, .03, .8));
    fragColor = alpha_blend(RED,   fragColor, hand(t,           60.0, xy0, .02, .9));
    fragColor = alpha_blend(BLACK, fragColor, sdf_to_alpha(length(xy0)-.04));
}

