vec3 portal( vec2 I)
{
    vec4 O;
    float tes=iTime*0.01;
    float r = length(I -= (O.xy = iResolution.xy) * .5)/O.y,
    z = log(r) -tes;
    O = pow(1.-texture(iChannel0, vec2(z, sign(I)*atan(I.yy,abs(I))/3.14-z*.5-tes*.1))*r,
    vec4(3,2,1,1));
    return O.xyz;
}

