precision highp float;

const int MAX_STEPS = 256;
const float MINIMUM_HIT_DISTANCE = 0.1;
const float NORMAL_OFFSET = 0.005;
const float MAXIMUM_TRACE_DISTANCE = 1024.0;

/*

This shader was originally generated with https://bauble.studio, but
I optimized the shader by hand and colorized it inside Shadertoy.

The original Bauble source that I used to iterate on shapes and values:

    (defn quantize [x y]
      (x * y | round / y))

    (defn wave [angle period phase magnitude]
      (sin ((~(. (* ,p (rotate_y (* ,angle ,tau))) :x) / 100 * period) + (tau * phase)) * magnitude))

    (half-space :-y
      (+ (wave 0.10 1.93 0.45 1.0)
         (wave 0.10 5.33 1.27 1.0)
         (wave 0.54 4.50 0.76 1.29)
         (wave 2.43 4.72 -1.87 0.94)
         (wave 2.38 6.74 -1.87 0.94)
         (p.z / -50)
         ((/ (length p.xz / 100)) * -4.5)
      | remap+
      | quantize 2
      * 10
      )
    | union
      (half-space :-y -36.6
      | bounded-offset 1 (perlin (vec3 (~(. (* (rotate_y -0.33) ,p) :xz) / [2 14]) t))
      | color [0 0 1]
      | fresnel)
    | intersect :r 5 (line [0 86 0] [0 100 0] 197)
    | slow 0.5)

I was planning to shade it normally, but I really liked the look of Bauble's
debug convergence view for this scene, so I decided to port it to Shadertoy
and use the raymarching steps as an input for the shading. This gives the shader
a nice, free "glow" effect. But because of this, it only looks good from a few
angles -- if you look straight down at the model, it's far too dark.

*/

mat3 rotate_y(float angle) {
  float s = sin(angle);
  float c = cos(angle);
  return mat3(
    c, 0.0, s,
    0.0, 1.0, 0.0,
    -s, 0.0, c);
}

// Perlin noise functions by Stefan Gustavson
// https://github.com/stegu/webgl-noise
vec3 fade3(vec3 t) {
  return t*t*t*(t * (t * 6.0 - 15.0) + 10.0);
}
vec4 taylor_inv_sqrt4(vec4 r) {
  return 1.79284291400159 - 0.85373472095314 * r;
}
vec3 mod289_3(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}
vec4 mod289_4(vec4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}
vec4 permute4(vec4 x) {
  return mod289_4(((x * 34.0) + 10.0) * x);
}
float perlin3(vec3 P) {
  vec3 Pi0 = floor(P); // Integer part for indexing
  vec3 Pi1 = Pi0 + vec3(1.0); // Integer part + 1
  Pi0 = mod289_3(Pi0);
  Pi1 = mod289_3(Pi1);
  vec3 Pf0 = fract(P); // Fractional part for interpolation
  vec3 Pf1 = Pf0 - vec3(1.0); // Fractional part - 1.0
  vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
  vec4 iy = vec4(Pi0.yy, Pi1.yy);
  vec4 iz0 = Pi0.zzzz;
  vec4 iz1 = Pi1.zzzz;

  vec4 ixy = permute4(permute4(ix) + iy);
  vec4 ixy0 = permute4(ixy + iz0);
  vec4 ixy1 = permute4(ixy + iz1);

  vec4 gx0 = ixy0 * (1.0 / 7.0);
  vec4 gy0 = fract(floor(gx0) * (1.0 / 7.0)) - 0.5;
  gx0 = fract(gx0);
  vec4 gz0 = vec4(0.5) - abs(gx0) - abs(gy0);
  vec4 sz0 = step(gz0, vec4(0.0));
  gx0 -= sz0 * (step(0.0, gx0) - 0.5);
  gy0 -= sz0 * (step(0.0, gy0) - 0.5);

  vec4 gx1 = ixy1 * (1.0 / 7.0);
  vec4 gy1 = fract(floor(gx1) * (1.0 / 7.0)) - 0.5;
  gx1 = fract(gx1);
  vec4 gz1 = vec4(0.5) - abs(gx1) - abs(gy1);
  vec4 sz1 = step(gz1, vec4(0.0));
  gx1 -= sz1 * (step(0.0, gx1) - 0.5);
  gy1 -= sz1 * (step(0.0, gy1) - 0.5);

  vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
  vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
  vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
  vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
  vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
  vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
  vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
  vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

  vec4 norm0 = taylor_inv_sqrt4(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
  g000 *= norm0.x;
  g010 *= norm0.y;
  g100 *= norm0.z;
  g110 *= norm0.w;
  vec4 norm1 = taylor_inv_sqrt4(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
  g001 *= norm1.x;
  g011 *= norm1.y;
  g101 *= norm1.z;
  g111 *= norm1.w;

  float n000 = dot(g000, Pf0);
  float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
  float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
  float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
  float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
  float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
  float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
  float n111 = dot(g111, Pf1);

  vec3 fade_xyz = fade3(Pf0);
  vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
  vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
  float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x);
  return 2.2 * n_xyz;
}

float lake(vec3 p) {
  float mag = 0.2;
  float threshold = 1.0;
  vec3 p1 = (p - vec3(0.0, -36.60, 0.0));
  float boundary_distance = (p1.y - mag);
  if (boundary_distance < threshold) {
    vec3 p1 = (p - vec3(0.0, -36.6, 0.0));
    vec3 rotated = rotate_y(-0.33) * p;
    return p1.y - mag * (
      perlin3(vec3(
        (rotated - vec3(0.0, 0.0, 10.0 * iTime)).xz / vec2(2.0, 14.0),
        sin(iTime)))
      + 0.25 * perlin3(vec3(4.0 * (rotated - vec3(0.0, 0.0, 10.0 * iTime)).xz / vec2(2.0, 14.0), iTime))
      );
  } else {
    return boundary_distance;
  }
}

const float TAU = 6.283185;

float quantize(float x, float y) {
  return round(x * y) / x;
}

float remap(float x) {
  return 0.5 * (x + 1.0);
}

float wave(vec3 p, float angle, float period, float phase, float magnitude) {
  float x = (p * rotate_y(angle * TAU)).x;
  return sin(x * 0.01 * period + TAU * phase) * magnitude;
}

float landscape(vec3 p) {
  float elevation = 10.0 * quantize(2.0, remap(
      wave(p, 0.10, 1.93, 0.45, 1.0)
    + wave(p, 0.10, 5.33, 1.27, 1.0)
    + wave(p, 0.54, 4.50, 0.76, 1.29)
    + wave(p, 2.43, 4.72, -1.87, 0.94)
    + wave(p, 2.38, 6.74, -1.87, 0.94)
    - (p.z / 50.0)
    - (450.0 / length(p.xz))
    ));

  return 0.4 * (p.y - elevation);
}

float union_0(vec3 p) {
  return min(landscape(p), lake(p));
}

float s3d_line(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a, ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  return length(pa - ba * h);
}

vec3 fresnel(vec3 normal, vec3 P, vec3 camera_origin, vec3 color, float strength, float exponent) {
  vec3 view_dir = normalize(camera_origin - P);
  float fresnel = pow(1.0 - dot(normal, view_dir), exponent);
  return color * strength * fresnel;
}

vec3 union_color_0(int steps, vec3 normal, vec3 camera_origin, vec3 p) {
  if (landscape(p) < lake(p)) {
    return vec3(0.0, 0.5*smoothstep(0.0, 0.5, float(steps) / float(MAX_STEPS)), 0.0);
  } else {
    float glow = smoothstep(0.0, 0.4, float(steps) / float(MAX_STEPS));
    float sun = clamp(dot(normalize(reflect(p - camera_origin, normal)), normalize(vec3(0.5, 1.0, -5))), 0.0, 1.0);
    sun = smoothstep(0.99, 1.0, sun);
    return
      vec3(0.0, 0.0, glow * 1.0)
      + vec3(1.0, 0.5, 0.0) * sun
      + fresnel(normal, p, camera_origin, vec3(0.8, 0.3, 1.0), 0.5, 3.0);
  }
}

vec3 intersect_color_1(int steps, vec3 normal, vec3 camera_origin, vec3 p) {
  float d = union_0(p);
  float d2;
  int surface = 0;
  vec3 color;
  d2 = (s3d_line(p, vec3(0.0, 86.0, 0.0), vec3(0.0, 100.0, 0.0)) - 197.0);
  if (d2 > d) { d = d2; surface = 1; }

  switch (surface) {
    case 0:
      return union_color_0(steps, normal, camera_origin, p);
      break;
    case 1:
      // underside
      return vec3(0.0, 0.25*smoothstep(0.0, 0.5, float(steps) / float(MAX_STEPS)), 0.0);
      break;
  }
  return color;
}

float nearest_distance(vec3 p) {
  float b, h = 0.0;
  float r = 10.0;
  float a = union_0(p);
  b = s3d_line(p, vec3(0.0, 86.0, 0.0), vec3(0.0, 100.0, 0.0)) - 197.0;
  h = clamp(0.5 - 0.5 * (b - a) / r, 0.0, 1.0);
  a = mix(b, a, h) + r * h * (1.0 - h);
  return a;
}

vec3 calculate_normal(vec3 p) {
  const vec3 step = vec3(NORMAL_OFFSET, 0.0, 0.0);

  return normalize(vec3(
    nearest_distance(p + step.xyy) - nearest_distance(p - step.xyy),
    nearest_distance(p + step.yxy) - nearest_distance(p - step.yxy),
    nearest_distance(p + step.yyx) - nearest_distance(p - step.yyx)
  ));
}

vec3 march(vec3 ray_origin, vec3 ray_direction, out int steps) {
  float distance = 0.0;

  for (steps = 0; steps < MAX_STEPS; steps++) {
    vec3 p = ray_origin + distance * ray_direction;

    float nearest = nearest_distance(p);

    // TODO: this attenuation only works when we're
    // using march to render from the camera's point
    // of view, so we can't use the march function
    // as-is to render reflections. I don't know if
    // it's worth having.
    // if (nearest < distance * MINIMUM_HIT_DISTANCE * 0.01) {
    if (nearest < MINIMUM_HIT_DISTANCE || distance > MAXIMUM_TRACE_DISTANCE) {
      return p + nearest * ray_direction;
    }

    distance += nearest;
  }
  return ray_origin + distance * ray_direction;
}

vec3 nearest_color(vec3 p, vec3 camera_origin, int steps) {
  return intersect_color_1(steps, calculate_normal(p), camera_origin, p);
}

const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

vec3 ray_dir(float fov, vec2 size, vec2 pos) {
  vec2 xy = pos - size * 0.5;

  float cot_half_fov = tan((90.0 - fov * 0.5) * DEG_TO_RAD);
  float z = size.y * 0.5 * cot_half_fov;

  return normalize(vec3(xy, -z));
}

mat3 rotate_xy(vec2 angle) {
  vec2 c = cos(angle);
  vec2 s = sin(angle);

  return mat3(
    c.y      ,  0.0, -s.y,
    s.y * s.x,  c.x,  c.y * s.x,
    s.y * c.x, -s.x,  c.y * c.x
  );
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  const float gamma = 2.2;
  vec2 resolution = iResolution.xy;

  vec3 camera_origin = vec3(0.0, 0.0, 350.0);
  vec2 rotation = vec2(-0.125, -0.25);
  if (iMouse.z > 0.0) {
    vec2 mouse_offset = (iMouse.xy / resolution) - 0.5;
    mouse_offset *= vec2(-1.0, 0.5);
    rotation += mouse_offset;
  }
  mat3 camera_matrix = rotate_xy(rotation.yx);

  camera_origin = camera_matrix * camera_origin + vec3(0.0, -32.0, 0.0);

  int view_type = 1;

  vec3 dir = camera_matrix * ray_dir(45.0, resolution, gl_FragCoord.xy);

  const vec3 fog_color = vec3(0.15);

  int steps;
  vec3 hit = march(camera_origin, dir, steps);

  vec3 color;
  float depth = distance(camera_origin, hit);
  if (depth >= MAXIMUM_TRACE_DISTANCE) {
    float glow = float(steps) / float(MAX_STEPS);
    color = mix(vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, 0.2), gl_FragCoord.y / resolution.y);
    color += vec3(0.0, glow, glow);
  } else {
    color = nearest_color(hit, camera_origin, steps);
  }


  fragColor = vec4(pow(color, vec3(1.0 / gamma)), 1.0);
}

int maxSteps = 200;
//max steps the loop will take.  try setting this really low, like 2 steps.
float maxDepth = 10.0;
//camera far plane pretty much.  See line 42.  This is usually around 500
//but for this example we can make it tiny to boost performance.
float minDist = 0.001;
//distance from a surface at which the ray stops

float DF(vec3 p)
{
    return length(p)-1.0;
    //this is the distance function of a sphere.  Finds the distance to the centre,
    //then subtracts the radius of the sphere
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y*2.0;
    //default UVs are fragCoord/iResolution.xy, which gives 0-1 coords from left to right and bottom to top.
    //Non-default UVs.  Cartesian coordinates instead, (0,0) is the centre of the screen

    float depth = 0.0;
    //how far along the ray is currently
    vec3 o = vec3(0, -2, 0);
    //origin point of the rays.  Camera position.
    vec3 dirV = vec3(uv.x, 1.0, uv.y);
    //Directional vector.  Added to the origin vector, to get a point in space offset from the camera
    //You may want to normalize this vector to avoid future issues.

    for (int i = 0; i < maxSteps; i++)
    {
        vec3 p = o + dirV * depth;
        //This is a ray.  
        //p is a point in space of a ray going in direction dirV, starting from o, with a length of depth
        float d = DF(p);
        //finding the scene distance function

        depth += d;
        //now we step forward by the minimum distance to the scene

        if (depth > maxDepth)
        //Effectively camera far plane.  It's not completely necessary but it provides 
        //a performance boost and prevents random noise in the sky
        {
            break;
        }

        if (d < minDist)
        //Stop the ray once it hits something.  Important for performance to stop the ray from
        //digging into a surface after it's close enough.  Sub pixel precision can almost always be discarded
        //and in fact, removing sub pixel precision tends to reduce noise in complex geometry.
        {
            break;
        }
    }

    //screen output
    fragColor = vec4(vec3(depth*2.0-2.0 /*bit of math that shifts the depth 
    to go from 0-1 from the front to the edge of the sphere.*/),1.0);
}

// Complex Rational Functions, mla, 2022.
//
// ie. f(z) = F(z)/G(z) where F and G are polynomials so
// f has zeros for roots of F and poles for roots of G. 
//
// Here we set the roots of the two polynomials (drag around with the mouse)
// and calculate F & G from them (ie. F(z) = (z-z0)(z-z1)... for roots z0,z1,...)
//
// Coloring shows arg(w), with contours shown for log(w)
//
// 'z' key adds an extra (unmoveable) zero at 0
// 'i' key shows 1/f(z)
// 'x' key inverts shading of magnitude

vec2 polyeval(int off, vec2 z) {
  vec2 res = vec2(1,0);
  for (int i = 0; i < N; i++) {
    res = cmul(res,z-getselection(2*i+off));
  }
  if (key(CHAR_Z) && off == 0) res = cmul(res,z);
  return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    setscale();
    vec2 z = map(fragCoord);

    vec3 col1 = vec3(1,1,0);
    vec3 col2 = vec3(0,1,1);
    vec3 neutral = vec3(1,0,0);

    vec2 num = polyeval(0,z);
    vec2 denom = polyeval(1,z);
    vec2 w = cdiv(num,denom);
    if (!key(CHAR_R)) {
      float t = 0.25*iTime;
      w = cmul(w,vec2(cos(t),sin(t)));
    }
    //if (key(CHAR_I)) w = cinv(w);
    w = clog(w);
    if (key(CHAR_I)) w = -w;
    float alpha = w.y/PI;

    vec3 color;

    if (alpha < 0.0) color = mix(neutral, col1, -alpha);
    else color = mix(neutral, col2, alpha);

    float t = 0.5+atan(w.x)/PI;
    vec2 uv = 10.0*w/PI;
    float px = fwidth(uv.x);
    uv = fract(uv);
    uv = min(uv,1.0-uv);
    if (key(CHAR_X)) t = 1.0-t;
    color = mix(vec3(t),color,smoothstep(0.0,px,min(uv.x,uv.y)));
    color = pow(color,vec3(0.4545));

    // Output to screen
    fragColor = vec4(color,1.0);
}

