void mainImage(out vec4 t, vec2 uv) {
  setscale();
  int i = int(uv.x); // Column number
  int j = int(uv.y); // Row
  if (iFrame == 0) {
    t = vec4(0);
  } else {
    t = texelFetch(iChannel2,ivec2(i,j),0); // Get current state   
  }  
  if (j == 0) {
    // First row - key count
    // Do key repeat here as well?
    float w1 = keystate(i,0).x;
    if (w1 != t.w) {
      t.w = w1;
      // Don't count key changes when ALT pressed
      if (keystate(KEY_ALT,0).x == 0.0 && w1 != 0.0) {
        t.x++; t.y = iTime;
      }
    }
    // Key repeat - only when shader is running!
    // Should have longer pause after first key press.
    if (t.w != 0.0 && keystate(KEY_ALT,0).x == 0.0 && iTime-t.y > 0.25) {
      t.x++; t.y = iTime;
    }
  } else if (j == 1) {
    if (i == 0 && iMouse.z != 0.0) t.xy = iMouse.xy;
    if (i == 1 && iMouse.z == 0.0) t.xy = iMouse.xy;
  } else if (j == 2 && key(i)) {
    // Record mouse position when key pressed
    t = vec4(iMouse.xy,0,iTime);
  } else if (j == 3) {
      // Cell 0 holds currently selected item, 0 = no selection
    if (i == 0 && iFrame != 0) {
      if (t.x == 0.0 && iMouse.z > 0.0) {
        vec2 mouse = map(iMouse.xy);
        // No selection, but mouse now down
        for (int i = nselections; i >= 1; i--) {
          // Select topmost item (assume drawn 1..N)
          vec2 p = texelFetch(iChannel2,ivec2(i,3),0).xy; // Get current state
          if (distance(mouse,p) < scale*0.05) {
            t.x = float(i);
            break;
          }
        }
      } else if (t.x != 0.0 && iMouse.z <= 0.0) {
        // Clear selection if mouse up.
        t.x = 0.0;
      }
    } else if (iFrame == 0 || t.z != 1234.0) {
      t.z = 1234.0;
      t.xy = initselection(i-1); // Current coordinates
    } else {
      vec4 t0 = texelFetch(iChannel2,ivec2(0,3),0); // Get current state
      if (int(t0.x) == i && iMouse.z > 0.0) {
        // If currently selected, copy mouse position
        t.xy = map(iMouse.xy);
      }
    }
  }
}

