float gT;


struct ray_t {
    vec3  ro;
    vec3  rd;
    float contrib;
    float side;           // 1 = external -1 = internal
};

struct hit_t {
    bool  hit;
    vec3  pnt;
    vec3  nrm;
    uint  mtl;
    float t;    // distance along ray
};

void rayVsSphere(in ray_t ray, in vec3 c, float r, uint mtl, inout hit_t hit) {

    vec3 ro = ray.ro - c;
    vec3 rd = ray.rd;

    float A = dot(rd, rd);
    float B = dot(ro, rd) * 2.0;
    float C = dot(ro, ro) - (r * r);

    vec2 roots;
    if (!solveQuadratic(vec3(A, B, C), roots)) {
        return;
    }

    if (roots.y < 0.0) {
        // both hits are before the start of the ray
        return;
    }

    if (roots.x > 0.0) {
        if (roots.x > hit.t) {
            return;
        }
        // good hit
        hit.hit = true;
        hit.t   = roots.x;
        hit.mtl = mtl;
        hit.pnt = ray.ro + hit.t * ray.rd;
        hit.nrm = (hit.pnt - c) / r;
        return;
    }

    if (roots.y < hit.t) {
        // good hit
        hit.hit = true;
        hit.t   = roots.y;
        hit.mtl = mtl;
        hit.pnt = ray.ro + hit.t * ray.rd;
        hit.nrm = (hit.pnt - c) / r;
        return;
    }
}

