    hit.t   = 1e9;
    hit.hit = false;

    rayVsSphere(ray,  v0, 1.0, 1u, hit);

    return hit;
}

//-----------------------------------------------------------
// https://en.wikipedia.org/wiki/Schlick%27s_approximation
float schlickR0(in float n1, in float n2) {
    // same if n1 and n2 are swapped.
    float q  = (n1 - n2) / (n1 + n2);
    float q2 = q * q;
    return q2;
}
float schlick(in float R0, in float cosTheta) {
    float q  = 1.0 - cosTheta;
    float q5 = q * q * q * q * q;
    return R0 + ((1.0 - R0) * q5);
}

//-----------------------------------------------------------

void mainImage(out vec4 RGBA, in vec2 XY) {
    vec2 xy = worldFromScreen(XY);
    vec2 m  = worldFromScreen(iMouse.xy);
    float ssEps = 3.0 / MINRES;
    float ssLw  = 2.0 / MINRES;

    gT = iTime * 0.2 + pi / 2.0;

    if (length(iMouse.xy) < 40.0) {
        m = vec2(cos(gT), sin(gT)) * 1.1 - vX.xy * 1.2;
    }

    float c = 0.0;
    float a = 1.0;

    ray_t r0;
    r0.ro      = vec3(m.x, m.y, 0.0);
    r0.rd      = vX;
    r0.contrib = 1.0;
    r0.side    = length(r0.ro) > 1.0 ? 1.0 : -1.0;
    QEnqueue(r0);

    float R0 = schlickR0(iorA, iorB); 

    uint rayCount = 0u;

    while(!QIsEmpty()) {
        ray_t r = QDequeue();
        hit_t h = rayVsScene(r);
        rayCount += 1u;

        if (!h.hit) {
            h.pnt = r.ro + r.rd * 1e5;
        }
        else {
            bool addMoreRays = true;
            addMoreRays = addMoreRays && (QSpaceLeft() > 2u);
            addMoreRays = addMoreRays && (r.contrib > gMinContrib);
            addMoreRays = addMoreRays && (rayCount < gMaxRays);
            if (addMoreRays) {
                vec3 hitFacingNormal = h.nrm * r.side;
                float eta = pow(iorA / iorB, r.side);
                ray_t rTrn;
                rTrn.ro      = h.pnt - hitFacingNormal * 0.0001;
                rTrn.rd      = refract(r.rd, hitFacingNormal, eta);
                if (rTrn.rd == v0) {
                    // total internal
                    rTrn.contrib = 0.0;
                }
                else {
                    float R = schlick(R0, dot(r.rd, -hitFacingNormal));
                    if (r0.ro.y < 0.0) {
                        R = 0.5;
                    }
                    rTrn.contrib = r.contrib * (1.0 - R);
                    rTrn.side    = -r.side;
                    QEnqueue(rTrn);
                }

                ray_t rRfl;
                rRfl.ro      = h.pnt + hitFacingNormal * 0.0001;
                rRfl.rd      = reflect(r.rd, hitFacingNormal);
                rRfl.side    = r.side;
                rRfl.contrib = r.contrib - rTrn.contrib;
                QEnqueue(rRfl);
            }
        }

        float d;
        d = length(xy - h.pnt.xy);
        c += a * r.contrib * smoothstep(ssEps, 0.0, d - ssLw * 4.0);
        a *= 0.2 + 0.8 * smoothstep(0.0, ssLw * 7.0, d - ssLw * 7.0);
        d = sdSegment(xy, r.ro.xy, h.pnt.xy);
        c += a * r.contrib * smoothstep(ssEps, 0.0, d - ssLw * 4.0);
        a *= 0.2 + 0.8 * smoothstep(0.0, ssLw * 7.0, d - ssLw * 7.0);
    }

    RGBA.rgb = vec3(c);

    const vec3 sceneColor = vec3(0.1, 0.5, 0.7) * 0.04;

    RGBA.rgb += sceneColor * smoothstep(ssEps, 0.0, abs(length(xy) - 1.0) - ssLw);
    RGBA.rgb += sceneColor * smoothstep(ssEps, 0.0, abs(xy.y) - ssLw) * smoothstep(0.0, ssEps, length(xy) - 1.0);

    // boost darker colors
    RGBA.rgb = pow(RGBA.rgb, vec3(0.45));
    RGBA.a = 1.0;
}

