const vec3  vY = vec3(0.0, 1.0, 0.0);
const vec3  vZ = vec3(0.0, 0.0, 1.0);
const float pi = 3.14159265359;


//--------------------------------------------------------------------------------
const float ior_air     = 1.0003;
const float ior_ice     = 1.309;
const float ior_water   = 1.333;
