const float ior_diamond = 2.42;


//--------------------------------------------------------------------------------
// #define Q_TYPE and Q_MAX_ENTRIES,
// then put Q_IMPLEMENMTATION.
// this is a basic ringbuffer.
// NO ERROR CHECKING
// orion elenzil 2022.
//
// Usage:
// #define Q_TYPE        <your type>
// #define Q_MAX_ENTRIES <size of rungbuffer you want. unsigned int>
// Q_IMPLEMENTATION
#define Q_IMPLEMENTATION                           \
const uint gQCapacity = Q_MAX_ENTRIES;             \
const uint gQNumSlots = gQCapacity + 1u;           \
Q_TYPE gQ[gQNumSlots];                             \
uint gQHead = 0u;                                  \
uint gQTail = 0u;                                  \
                                                   \
uint QCount() {                                    \
	if (gQHead >= gQTail) {                        \
		return gQHead - gQTail;                    \
	}                                              \
	else {                                         \
		return gQNumSlots - (gQTail - gQHead);     \
	}                                              \
}                                                  \
                                                   \
uint QSpaceLeft() {                                \
	return gQCapacity - QCount();                  \
}                                                  \
                                                   \
bool QIsFull() {                                   \
	return QSpaceLeft() == 0u;                     \
}                                                  \
                                                   \
bool QIsEmpty() {                                  \
	return QCount() == 0u;                         \
}                                                  \
                                                   \
uint QEnqueueIndex() {                             \
	gQHead = (gQHead + 1u) % gQNumSlots;           \
    return gQHead;                                 \
}                                                  \
                                                   \
uint QDequeueIndex() {                             \
	gQTail = (gQTail + 1u) % gQNumSlots;           \
    return gQTail;                                 \
}                                                  \
                                                   \
void QEnqueue(Q_TYPE item) {                       \
	gQ[QEnqueueIndex()] = item;                    \
}                                                  \
                                                   \
Q_TYPE QDequeue() {                                \
	return gQ[QDequeueIndex()];                    \
}
