// returns false if there are no real roots.
// returns true if there are one or two real roots.
// roots.x <= roots.y.
bool solveQuadratic(in vec3 coeffs, out vec2 roots) {
    const uint A = 0u;
    const uint B = 1u;
    const uint C = 2u;

    // (-B +- sqrt(BB - 4AC)) / 2A

    float disc = (coeffs[B] * coeffs[B]) - (4.0 * coeffs[A] * coeffs[C]);

    if (disc < 0.0) {
        return false;
    }

    float sqrt_disc = sqrt(disc);

    roots.x = (-coeffs[B] - sqrt_disc) / (2.0 * coeffs[A]);
    roots.y = (-coeffs[B] + sqrt_disc) / (2.0 * coeffs[A]);

    if (roots.y < roots.x) {
        roots.xy = roots.yx;
    }

    return true;
}


//--------------------------------------------------------------------------------
// From IQ - https://iquilezles.org/articles/distfunctions2d
float sdSegment(in vec2 p, in vec2 a, in vec2 b)
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

