// move horizontally, with circles and random offset
vec2 move(float t)
{
    vec2 pos = vec2(0);
    float angle = t*12.;
    float radius = .1;
    float time = t*5.;
    float index = floor(time);
    float anim = fract(time);
    float scroll = fract(t*speed);
    vec2 rng = mix(hash21(index), hash21(index+1.), anim);
    pos += (rng*2.-1.)*.5;
    pos.x += scroll*2.-1.;
    pos += vec2(cos(angle),sin(angle))*radius;
    return pos;
}

// draw mask
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = 2.*(fragCoord-iResolution.xy/2.)/iResolution.y;

    // mask
    float scroll = fract(iTime*speed);
    float thin = .1+.05*sin(iTime*16.);
    float dist = sdSegment(pos, move(iTime-iTimeDelta), move(iTime));
    float mask = smoothstep(thin,.0,dist);

    // leaks
    float time = iTime*100.;
    float anim = fract(time);
    float index = floor(time);
    float wave = sin(anim*3.14);
    vec3 noise = texture(iChannel2, uv).rgb;
    vec3 normal = texture(iChannel1, uv).rgb;
    vec2 target = (hash21(index)*2.-1.);
    float d = length(pos-target);
    float gravity = wave * smoothstep(.1,.0,d) * 10.;
    vec2 offset = vec2(0,1)/iResolution.xy;
    vec4 frame = texture(iChannel0, uv+offset);
    float leak = frame.g;
    gravity *= smoothstep(.6, 1., noise.g);
    gravity *= max(0., dot(normal, -vec3(0,1,0)));
    uv.y += noise.b*leak/iResolution.y;
    uv -= leak*normal.xy/iResolution.xy;

    // frame buffer
    frame = texture(iChannel0, uv);
    mask = mask * 0.1 + frame.r;
    fragColor = vec4(mask, frame.g+gravity, 0, 0);
    fragColor *= step(.01, scroll);
}

