         U = ( u - .5*R ) / R.y, F;
     O = vec4(1);     
    if (abs(U.x)>.5) return;                                        // out of bounds
    int m=1, z=0, t = int(iTime)%8;

    for( ; z < 1+min(t,8-t); z++ ) {                                // === fractal loop
        U *= 3.; 
        ivec2 I = ivec2(mod(U+1.5,3.)) - 1;                         // sub-tile id
        if ( m==2 && (I.x+I.y+2)%2 < 1 ) U = vec2(U.y,-U.x);        // rot  // +2 for MacOS
        if ( I.y==0 && I.x!=0 ) U = float(I.x)*vec2(-U.y,U.x);      // rot
                                                //  ( c = tile id, I = sub-tile pos )   sub-tile id 
        m = int[]( -3,2,1,3,4,3,1,2,3, -3,-3,-3,2,4,2,3,3,3, -3,2,3,3,4,3,1,2,3, 
                   0,0,-5,0,6,0,5,0,0, 0,0,6, 0,6,0, 5,0,0,  0,0,6, 0,6,0, 6,0,0 ) [I.x+3*I.y+9*m-5];
        if ( m==0 )  return;                                        // empty area
        if ( m < 0 ) U = -U, m = -m;                                // rot2 ( = sym )
    }   
    F = fract(U+1.5); 
    float y = ( 1. + smoothstep(1.,0., m!=5 ? F.x : F.x/2. ) ) / 3.,// --- draw tile elements
          d =   m==1 ? F.y - y                                      // meander elements
              : m==2 ? F.y + y-1.       
              : m==3 ? length(F) - .67  
              : m==4 ? F.y - y                                      // diagonal elements
              : m==5 ? F.y - 4.*y+2.    
              :        F.y+F.x - 1.;              
    O = sqrt( abs(d) * R.yyyy/pow(3.,float(z)) );                   // draw with AA + final sRGB
}

#define catSize 4.3
#define NormalizeScale uv -= vec2(0.5, 0.5); uv *= catSize; uv += vec2(0.5, 0.5);

void storeValue (in ivec2 v, in vec4 va, inout vec4 fragColor, in ivec2 fragCoord)
{
    fragColor = ( v.x == fragCoord.x && v.y == fragCoord.y ) ? va : fragColor;
}

vec2 uvRotate(vec2 uv, vec2 offset, float mul)
{
    uv -= vec2(offset.x, offset.y);
    uv *= vec2(3.7,3.7);
    //fake rotate
    //uv.x /= 1.0 * sin(iTime * 2.5 * mul);
    uv += vec2(offset.x, offset.y);

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 finaly = vec4(0.0);
    ivec2 ifragCoord = ivec2( fragCoord-0.5 );
    vec4 badCatUv = fetchData(iChannel1, badCatPos);
    vec2 uv = fragCoord/iResolution.xy;
    vec2 buv = uv;
    vec2 m = uv;
    vec2 hudUv = uv;

    vec2 bcat = uv;

    bcat -= badCatUv.xy;

    NormalizeScale;

    vec4 pos = fetchData(iChannel1, position);
    vec4 _jump = fetchData(iChannel1, jump);
    float dir = fetchData(iChannel1, direction).r;
    vec4 coinY = fetchData(iChannel1, coinsY);
    vec4 coinX = fetchData(iChannel1, coinsX);
    vec4 catP = fetchData(iChannel1, catPos);
    vec4 sbcatP = fetchData(iChannel1, sbadCatPos);
    vec4 cl = fetchData(iChannel1, CL);
    vec4 stg = fetchData(iChannel1, stage);

    vec4 coin = vec4(0.0);

    coin += texture( iChannel3, uvRotate(m - vec2(coinX.x,coinY.x),  vec2(0.5,0.5),cl.w)) ;
    coin += texture( iChannel3, uvRotate(m - vec2(coinX.y,coinY.y),  vec2(0.5,0.5),cl.w)) ;
    coin += texture( iChannel3, uvRotate(m - vec2(coinX.z,coinY.z),  vec2(0.5,0.5),cl.w)) ;

    //UI Coin
    coin += texture( iChannel3, hudUv * 9.5- vec2(-0.35, 8.32)); 

    vec2 shdUv = uv;
    shdUv.y *= -1.0;
    shdUv.x -= pos.x;
    shdUv.xy -= vec2(.45, 1.15 + pos.y * 0.1);
    shdUv.x *= -dir;

    vec2 catUv = uv - pos.xy; 
    catUv -= vec2(0.5,0.5);
    catUv.x *= -dir;
    shdUv.x += 0.2;
    catUv += vec2(0.5,0.5);

    bcat -= vec2(0.5,0.5);
    bcat *= 4.3;
    bcat.x *= badCatUv.w;
    bcat += vec2(0.5,0.5);

    vec4 cat = texture( iChannel0, catUv );
    vec4 badCat = texture(iChannel0, bcat) * vec4(0.0,0.0,0.0,1.0);
    vec4 badCatShadow = texture(iChannel0, bcat * vec2(1.0,-2.9) - vec2(0.,-0.8)) * vec4(0.0,0.0,0.0,0.5);
    float eLR = circleA(bcat, vec2(0.65,0.5), 0.04);
    eLR += circleA(bcat, vec2(0.8,0.5), 0.04);
    badCat += eLR * vec4(1.0,0.0,0.0,1.0);
    badCat += badCatShadow;


    vec3 back = texture( iChannel2, buv ).rgb;

    //add pink if danger distance
    if(sbcatP.x >= 0.35)
    {
        back.rgb += vec3(0.5,0.0,0.5);
    }

    //add red if hit damage 
    if(sbcatP.x >= 0.55)
    {
        back.rgb += vec3(1.0,0.0,-1.0);
    }

    shdUv.y *= 2.9;
    vec4 catShadow = texture( iChannel0, shdUv + vec2(0.3, -0.4) ) ;


    cat.rgb = mix( catShadow.rgb * vec3(0,0,0) * back.r, cat.rgb, cat.a );
    //Controll of shadow density 
    cat.a += catShadow.a * 0.5 ; //add jump modify to change density
    cat.a = clamp(cat.a,0.0,1.0);

    finaly.rgb = mix(back, coin.rgb, coin.a);
    finaly.rgb = mix(finaly.rgb, cat.rgb, cat.a);
    finaly.rgb = mix(finaly.rgb, badCat.rgb, badCat.a);

    //UI lives 
    int count = int(cl.y);

    for(int i = 0; i < count; i++)
    {
        float cly = float(i);

        //HEART BLOCK------------------

        vec2 uv2 = fragCoord/iResolution.xy;
        uv2.x *= 2.0;
        uv2 /= 0.15;
        uv2.x += 0.3;
        uv2.y -= 6.18;
        vec2 uv3 = uv2;

        uv2.x -= cly * 0.33;
        uv3.x -= cly * 0.33;

        uv2 += vec2(0.5,0.0) * uv2.y;
        uv3 -= vec2(0.5,0.0) * uv2.y;

        float hearth = clamp( circleA( uv2, vec2(0.60,0.29),0.1)
                        + circleA( uv3, vec2(0.40,0.29),0.1), 0.0, 1.0);

        //HEART BLOCK-------------------

        finaly += hearth * vec4(1.0,0.2,0.0,hearth);
    }

    if(stg.z == 0.0)
    {
        vec4 _coin =  texture( iChannel3, hudUv * 2.5- vec2(0.5, 1.57));
        finaly.rgb = mix(back.rgb, _coin.xyz * _coin.w, _coin.w)  ;
    }


    fragColor = clamp(vec4( vec3(finaly), 1.0), 0.0, 1.0);

}

