void difficult (float score, inout float multiple, inout float stage, inout float uitime)
{

    if(score == 10.)
    {
        multiple = 1.0;
        stage += 1.0;
        uitime = 1.5;
    }
    else if(score == 30.)
    {
        multiple = 1.5;
        stage += 1.0;
        uitime = 1.5;
    }
    else if(score == 50.)
    {
        multiple = 2.0;
        stage += 1.0;
        uitime = 1.5;
    }
    else if(score == 70.)
    {
        multiple = 2.5;
        stage += 1.0;
        uitime = 1.5;
    }
    else if(score == 100.)
    {
        multiple = 2.9;
        stage += 1.0;
        uitime = 1.5;
    }
    else if(score == 150.)
    {
        multiple = 3.5;
        stage += 1.0;
        uitime = 1.5;
