void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    bool  E = texelFetch(iChannel1, ivec2(KEY_ENTER, 0.), 0).x  > 0.0 ?  true : false;
    bool  S = texelFetch(iChannel1, ivec2(KEY_SPACE, 0.), 0).x  > 0.0 ?  true : false;
    float L = texelFetch(iChannel1, ivec2(KEY_LEFT,  0.), 0).x  > 0.0 ?  1.0 : 0.0;
    float R = texelFetch(iChannel1, ivec2(KEY_RIGHT, 0.), 0).x  > 0.0 ? -1.0 : 0.0;

    //Start Data

    vec3 catColorRandom = vec3( 0.1 + random(uv,iTime * iDate.z), 0.1 + random(uv,iTime + iDate.z), 0.1 + random(uv,iTime * iDate.w) );

    if (storeData(fragCoord, position))
        fragColor = vec4(0.0, 0.25, 0.0, 0.0 );
    if (storeData(fragCoord, jump))
        fragColor = vec4(1.0, 0.0, 1.0, 0.0 );
    if (storeData(fragCoord, direction))
        fragColor = vec4(-1.0, 0.0, 0.0, 0.0 );
    if (storeData(fragCoord, coinsX))
        fragColor = vec4(-0.37, 0.0, 0.37, 0.0 );
    if (storeData(fragCoord, coinsY))
        fragColor = vec4(1.5, 1.0, 1.5, 0.0 );
    if (storeData(fragCoord, CL))
        fragColor = vec4(0.0, 9.0, 1.0, 1.0 );
    if (storeData(fragCoord, catColor))
        fragColor = vec4(catColorRandom, 0.0);
    if (storeData(fragCoord, stage))
        fragColor = vec4(48, 1.5, 1.0, 0.0);

    ivec2 ifragCoord = ivec2( fragCoord-0.5 );
    vec4 pos = loadValue(position);
    vec4 _jump = loadValue(jump);
    vec4 dir = loadValue(direction);
    vec4 coinY = loadValue(coinsY);
    vec4 coinX = loadValue(coinsX);
    vec4 bCatPos = loadValue(badCatPos); 
    vec4 sbCatPos = loadValue(sbadCatPos);
    vec4 cl = loadValue(CL);
    vec4 ccr = loadValue(catColor);
    vec4 stg = loadValue(stage);

    if (iFrame > 0)  
    {
       if(stg.z == 2.0)
       {
           //Pasuse stage
       }
       else if(stg.z == 1.0)
       {
//CHARACTER SECTION--------------------------------

            if( L > 0. && R == 0. || L == 0. && R < 0. )
            {
                pos.x -= (L + R) * speed;
                dir.x = L + R;
            }

//----------------jump-----------------------------

            if( S && _jump.x == 0.0)
            {
                _jump.x = 1.0;
                _jump.y = jumpheight;
                // add jump iteration
                _jump.z = 1.0;
            }

            pos.y += _jump.y;

            // wasting jump energy
            if(_jump.x > 0.0)
            {
                _jump.y -= 0.005;
            }

            // emulate floor
            pos.y = clamp(pos.y, border.x, 1.0);

            // reset jump state
            if(pos.y == border.x)
            {
                if(_jump.z > 0.0)
                {
                    _jump.z -= 1.0;
                    _jump.y = jumpheight/3.0;
                }
                else
                {
                    _jump.x = 0.0;
                }
            }

            pos.x = clamp(pos.x, -1.8, 1.8);

//MOONCATCOINS SECTION--------------------------------

            coinY.xyz -= 0.015 * cl.w;

            if(coinY.x < -0.35)
            {
                coinY.x += 2.0;
            }
            if(coinY.y < -0.35)
            {
                coinY.y += 2.0;
            }
            if(coinY.z < -0.35)
            {
                coinY.z += 2.0;
            }

            sbCatPos.y = 1.-distance(pos.xy,vec2(coinX.y,coinY.y) * 4.3);

            if(sbCatPos.y >= 0.5)
            {

                coinY.y = 1.0;
                cl.x += 1.0;
                difficult( cl.x, cl.w, stg.x, stg.y);
            }

            sbCatPos.z = 1.-distance(pos.xy,vec2(coinX.x,coinY.x) * 4.3);

            if(sbCatPos.z >= 0.5)
            {
                coinY.x = 1.0;
                cl.x += 1.0;
                difficult( cl.x, cl.w, stg.x, stg.y);
            }

            sbCatPos.w = 1.-distance(pos.xy,vec2(coinX.z,coinY.z) * 4.3);

            if(sbCatPos.w >= 0.5)
            {
                coinY.z = 1.0;
                cl.x += 1.0;
                difficult( cl.x, cl.w, stg.x, stg.y);
            }

            coinX.xyz += (L + R) * speed * 0.15;

            coinX.x = clamp(coinX.x, -0.37-0.3, -0.37+0.25);
            coinX.y = clamp(coinX.y, 0.0-0.3, 0.0+0.25);
            coinX.z = clamp(coinX.z, 0.37-0.3, 0.37+0.25);

// STAGE -----------------------------------------
            if(stg.y > 0.0)
                stg.y -= 0.01;

// BAD CAT SECTION--------------------------------
            bCatPos.xy = vec2(-0.35,-0.35) + vec2(2. * sin(iTime + 4.0) * .5 *  cl.w, 0.0);
            vec2 zbCatPos = bCatPos.xy * 4.3;

            //badCatDirection
            bCatPos.w = sin(iTime) > 0. ? 1.0 : -1.0;

            //distance to character
            sbCatPos.x = 1.- distance(pos.xy, zbCatPos.xy) ;

            if(sbCatPos.x >= 0.6)
            {
                //reset pos
                pos.x = 0.0; 
                pos.y = 0.0;

                //minus life
                cl.y -= 1.0;

                //resetcoins pos
                coinX.x = -0.37;
                coinX.y = 0.0;
                coinX.z = 0.37;

                //reset game is hp left
                if(cl.y <= 0.)
                {

                    //
                    cl.w = 1.;

                    //start stage _1 and alphatext
                    stg.x = 48.;
                    stg.y = 1.5;

                    //game over
                    stg.z = 0.0;

                    ccr.xyz = vec3( 0.1 + random(uv, fract(iTime)), 0.1 + random(uv, fract(iDate.w)), 0.1 + random(uv, fract(iDate.z)) );
                }


            }
       }
       //Restart
       else if(stg.z == 0.0)
       {
           if( E )
           {
               //game Stage
               stg.z = 1.0;
               //coins
               cl.x = 0.;
               //lives
               cl.y = 9.;
           }
       }


        saveValue( position,    vec4(pos),      fragColor,    ifragCoord );
        saveValue( jump,        vec4(_jump),    fragColor,    ifragCoord );
        saveValue( direction,   vec4(dir),      fragColor,    ifragCoord );
        saveValue( coinsY,      vec4(coinY),    fragColor,    ifragCoord );
        saveValue( coinsX,      vec4(coinX),    fragColor,    ifragCoord );
        saveValue( badCatPos,   vec4(bCatPos),  fragColor,    ifragCoord );
        saveValue( sbadCatPos,  vec4(sbCatPos), fragColor,    ifragCoord );
        saveValue( CL,          vec4(cl),       fragColor,    ifragCoord );
        saveValue( catColor,    vec4(ccr),      fragColor,    ifragCoord );
        saveValue( stage,       vec4(stg),      fragColor,    ifragCoord );


    }
}

