common_code:
float erp(float x, float k)
{
    return x < 0.5 ? pow(2.0*x, k)*0.5  : 1. - pow(2.0*(1.0-x), k) / 2.;
}


vec2 hash(vec2 p)
{
    vec3 p3 = fract(p.xyx * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float perlinNoise(vec2 p, float s)
{
    vec2 tlVal = hash(vec2(floor(p.x),  ceil(p.y)));
    vec2 blVal = hash(vec2(floor(p.x), floor(p.y)));
    vec2 trVal = hash(vec2( ceil(p.x),  ceil(p.y)));
    vec2 brVal = hash(vec2( ceil(p.x), floor(p.y)));

    vec2 tlOffset = p - vec2(floor(p.x),  ceil(p.y));
    vec2 blOffset = p - vec2(floor(p.x), floor(p.y));
    vec2 trOffset = p - vec2( ceil(p.x),  ceil(p.y));
    vec2 brOffset = p - vec2( ceil(p.x), floor(p.y));

    float tl = dot(tlOffset, tlVal);
    float bl = dot(blOffset, blVal);
    float tr = dot(trOffset, trVal);
    float br = dot(brOffset, brVal);

    float a = mix(bl, tl, erp(fract(p.y), s));
    float b = mix(br, tr, erp(fract(p.y), s));

    return mix(a, b, erp(fract(p.x), s)) / 2.0 + 0.5;
}



float fbm(in vec2 x, float s, in float H)
{    
    float G = exp2(-H);
    float f = 1.0;
    float a = 1.0;
    float t = 0.0;
    for( int i=0; i<8; i++ )
    {
        t += a*perlinNoise(f*x, s);
        f *= 2.0;
        a *= G;
    }
    return t;
}

vec4 blackbody(float x, float intensity) //By @FabriceNeyret2 (modified by me) https://www.shadertoy.com/view/4tdGWM
{
    vec4 O = vec4(0.0);
    float m = .01+5.*intensity,
        T = x*16000.; // absolute temperature (K)

    O -= O;

/*  // --- with physical units: (but math conditionning can be an issue)
    float h = 6.6e-34, k=1.4e-23, c=3e8; // Planck, Boltzmann, light speed  constants

    for (float i=0.; i<3.; i++) {  // +=.1 if you want to better sample the spectrum.
        float f = 4e14 * (1.+.5*i); 
        O[int(i)] += 1e7/m* 2.*(h*f*f*f)/(c*c) / (exp((h*f)/(k*T)) - 1.);  // Planck law
    }
*/
    // --- with normalized units:  f = 1 (red) to 2 (violet). 
    // const 19E3 also disappears if you normalized temperatures with 1 = 19000 K
     for (float i=0.; i<3.; i += .05) {  // +=.1 if you want to better sample the spectrum.
        float f = 1.+.5*i; 
        O[int(i)] += 10./m* (f*f*f) / (exp((19E3*f/T)) - 1.);  // Planck law
    }
    return O;
}

