vec3 blur(sampler2D tex, float size, int res, vec2 uv, float ratio)
{
    float div = 0.0;
    vec3 accumulate = vec3(0.0);

    for(int iy = 0; iy < res; iy++)
    {
        float y = (float(iy) / float(res))*2.0 - 1.0;
        for(int ix = 0; ix < res; ix++)
        {
            float x = (float(ix) / float(res))*2.0 - 1.0;
