            float i = intensity(p);

            div += i;
            accumulate += pow(texture(tex, uv+p*size*vec2(1.0, ratio)).xyz, vec3(gamma)) * i;
        }
    }

    return pow(accumulate / vec3(div), vec3(1.0 / gamma));
}

