float spots(vec2 uv, float seed, float scale, float size) {
  float p2Zoom = 1./scale; //4.;
  float p2Cut = mix(0.9, 0.65, size); //crowded; //.7;

  float p3Zoom = p2Zoom * 3.;
  float p3Cut = p2Cut * .65;
  float p3 = nsimplex(uv*p3Zoom + 100.*p3Zoom, seed+129.327);
  //p3 = flatten(p3, 5.);
  p3 *= step(p3Cut, p3);
  p3 = normrange(p3, p3Cut, 1.0);

