  p2uv *= p2Zoom;
  p2uv += p3*.02;
  float p2 = nsimplex(p2uv, seed);
  //float p2Flat = flatten(p2, 5.);
  float p2SubCut1 = p2Cut - .1*(.2/scale);
  float p2SubCut2 = p2SubCut1 - .2*(.2/scale);
  float p2L1 = xnormrange(p2, p2SubCut1, p2Cut)*.8;
  float p2L2 = xnormrange(p2, p2SubCut2, p2SubCut1)*.4;
  p2 = step(p2Cut, p2)*.9 + p2L1 + p2L2;
  return p2;
}

struct noise_out {
  float v1;
  float v2;
  float v3;

  float l0;
  float l1;
  float l2;
  float l3;
  float l4;
  float l5;

  float l0Min;
  float l0Max;
};

noise_out getNoise(vec2 uv, float t) {
  float tseed = floor(t*0.1);
  float seed  = tseed;
  float seed2 = tseed + 373.297;
  float seed3 = tseed + 793.713;
  float seed4 = tseed + 127.139;
  float seed5 = tseed + 929.197;
  vec2 roll = -0.05*t*vec2(1.);
  // uv += roll;

  float v = 0.;
  float v1 = 0.;
  float v2 = 0.;
  float v3 = 0.;

  float rand = random(uv, seed);
  float p1 = rand;
  float p1Min = 0.5;
  float p1Max = 0.6;
  p1 = mix(p1Min, p1Max, p1);

  float c1 = .9-p1Min;
  float c2 = .7-p1Min;
  float c3 = -p1Min+.1;

  float p2a = spots(uv, seed       , .2, .1) * c1;
  float p2b = spots(uv, seed+1000. , .5, .3) * c1;
  float p2c = spots(uv, seed+2000. , .5, .3) * c1;
  float p4a = spots(uv, seed3      , .2, .5) * c3;
  float p4b = 0.;//spots(uv, seed3+1000., .2, .2) * c3;


  //float final = p1;
  //final = mixOverFiltered(p2c, final, p1Min, p1Max);
  //final = mixOverFiltered(p2a, final, p1Min, p1Max);
  //final = mixOverFiltered(p4a, final, p1Min, p1Max);
  //final = mixOverFiltered(p2b, final, p1Min, p1Max);
  //final = mixOverFiltered(p4b, final, p1Min, p1Max);
  //v = final;

  float layer0 = p1;
  float layer1 = p2c;
  float layer2 = p2a;
  float layer3 = p4a;
  float layer4 = p2b;
  //float layer5 = p4b;

  noise_out n;
  n.v1 = layer0;
  n.v2 = layer1;
  n.v3 = 1.+layer3;
  n.l0 = layer0;
  n.l1 = layer1;
  n.l2 = layer2;
  n.l3 = layer3;
  n.l4 = layer4;
  //n.l5 = layer5;
  n.l0Min = p1Min;
  n.l0Max = p1Max;

  return n;
}

vec3 withColors(noise_out n) {
  float final = n.l0;
  final = mixOverFiltered(n.l1, final, n.l0Min, n.l0Max);
  final = mixOverFiltered(n.l2, final, n.l0Min, n.l0Max);
  final = mixOverFiltered(n.l3, final, n.l0Min, n.l0Max);
  final = mixOverFiltered(n.l4, final, n.l0Min, n.l0Max);
  //final = mixOverFiltered(n.l5, final, n.l0Min, n.l0Max);
  final = sat(final);
  //vec3 ORA = vec3(.745,.486,.216);
  //vec3 ORA = vec3(0.8078, 0.5176, 0.2667);
  vec3 ORA = vec3(0.8078, 0.5373, 0.302);
  //final = n.l0;
  float inDiff = normrange(final, n.l0Min, n.l0Max);
  float outDiffUp = xclampnorm(final - n.l0Max, 0.0, 1.-n.l0Max);
  float outDiffDown = xclampnorm(n.l0Min - final, 0.0, n.l0Min);
  float outDiff = norm(outDiffUp-outDiffDown);
  vec3 minOra = mix(ORA, BLA, 0.1);
  vec3 maxOra = mix(ORA, WHI, 0.1);
  //vec3 c = mix3(minOra, ORA, maxOra, denorm(inDiff));
  vec3 c = mix(minOra, maxOra, inDiff);
  minOra = mix(ORA, BLA, 0.85);
  maxOra = mix(ORA, WHI, 0.97);
  c = mix3(minOra, c, maxOra, denorm(outDiff));
  return c;
}


#ifndef SHADERTOY
uniform sampler2D iChannel0;
uniform float iTime;
uniform vec2 iResolution2D;
#define iResolution vec4(iResolution2D, 0., 0.)
out vec4 outColor;
