void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from -0.5 to 0.5)
  float mx = max(iResolution.x, iResolution.y);
  vec2 ct = iResolution.xy / mx / 2.0;
  vec2 uv = fragCoord/mx - ct;
  vec2 uv2 = fragCoord / iResolution.xy - .5;

  float t = iTime;
  t *= 0.35;


  vec3 sims[30];

  float allZoom = .35;
  vec2 uvZoom1 = uv / allZoom;
  vec2 uvZoom2 = uvZoom1 / 2.;
  vec2 uvZoom3 = uvZoom2 / 2.;
  float textureZoom = 3.;

  noise_out noises1 = getNoise(uvZoom1, t);
  noise_out noises2 = getNoise(uvZoom2, t);
  noise_out noises3 = getNoise(uvZoom3, t);

  vec3 finalNoiseZoomed1 = withColors(noises1);
  vec3 finalNoiseZoomed2 = withColors(noises2);
  vec3 finalNoiseZoomed3 = withColors(noises3);

  vec3 textureZoomed1 = texture(iChannel0, uvZoom1/textureZoom).rgb;
  vec3 textureZoomed2 = texture(iChannel0, uvZoom2/textureZoom).rgb;
  vec3 textureZoomed3 = texture(iChannel0, uvZoom3/textureZoom).rgb;

#ifdef GREYSCALE
  textureZoomed1 = togrey(textureZoomed1);
  textureZoomed2 = togrey(textureZoomed2);
  textureZoomed3 = togrey(textureZoomed3);
#endif

  int col0Count = 0;
  sims[(col0Count++)*2] = WHI * noises1.v1;
  sims[(col0Count++)*2] = WHI * noises1.v1;
  sims[(col0Count++)*2] = WHI * noises1.v2;
  sims[(col0Count++)*2] = WHI * noises1.v2;
  sims[(col0Count++)*2] = WHI * noises1.v3;
  sims[(col0Count++)*2] = WHI * noises1.v3;

  int col1Count = 0;
#ifdef PREVIEW_NOTEXTURE
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed2;
  sims[(col1Count++)*2+1] = finalNoiseZoomed3;
#elif PREVIEW_COMPARISON
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = textureZoomed1;
  sims[(col1Count++)*2+1] = textureZoomed1;
  sims[(col1Count++)*2+1] = textureZoomed1;
#else
  sims[(col1Count++)*2+1] = finalNoiseZoomed1;
  sims[(col1Count++)*2+1] = textureZoomed1;
  sims[(col1Count++)*2+1] = finalNoiseZoomed2;
  sims[(col1Count++)*2+1] = textureZoomed2;
  sims[(col1Count++)*2+1] = finalNoiseZoomed3;
  sims[(col1Count++)*2+1] = textureZoomed3;
#endif

  uv2 += .5;
  float gridWidth = 2.0;
  float gridHeight = 6.0;
  float gridX = floor(uv2.x*gridWidth);
  float gridY = floor((1.-uv2.y)*gridHeight);
  int gridInx = int(floor(gridY*gridWidth + gridX));
  vec3 c;
  int count = col0Count + col1Count;
  for (int i = 0; i < count; i++) {
    c += sims[i] * (gridInx == i ? 1.0 : 0.0);
  }


  // Output to screen
  fragColor = vec4(c, 1.0);
}

#ifndef SHADERTOY
void main()
{
  mainImage(outColor, gl_FragCoord.xy);
}
#endif

#define resolution iResolution
#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 r = resolution.xy, u = (2.* fragCoord - r)/r.y;
    u.y = abs(u.y);
    u *= mat2(cos(35.), -sin(45.),sin(45.),cos(45.)) + sin(time) * 0.005;
    u.x *= 10.;
    float pos = floor(u.x+ time);
    fragColor.r = mod(pos, 2.) * sin(pos* 0.5 + time);
    fragColor.g = mod(pos, 2.) * sin(pos* 0.5 + time + .6 );
    fragColor.b = mod(pos, 2.) * sin(pos* 0.5 + time + .8);
    fragColor *= (1.-abs(u.y *0.7));
}

