// https://iquilezles.org/articles/distfunctions/
// vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
// return min(max(d.x,d.y),0.0) + length(max(d,0.0));
float sdCylinder(vec3 p, float r, float h){
    float xz = length(p.xz)-r;
    float y = abs(p.y)-h;
    float m = max(xz,y);
    float e = length(max(vec2(xz,y),0.));
    return min(m,0.)+e;
}

float sdPipe(vec3 p, float r, float h, float t){
    float xz = abs(length(p.xz)-r+t)-t;
    float y = abs(p.y)-h;
    float m = max(xz,y);
    float e = length(max(vec2(xz,y),0.));
    return min(m,0.)+e;
}

// https://www.shadertoy.com/view/Ml3fWj
// vec3 q = abs(p)-h;
// return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
float sdElongatedCylinder(vec3 p, vec3 s){
    p = abs(p);
    p.x -= s.x-s.y;
    p = max(p, 0.);

    float xz = length(p.xz)-s.y;
    float y = abs(p.y)-s.z;
    float m = max(xz,y);
    float e = length(max(vec2(xz,y),0.));
    return min(m,0.)+e;
}

float sdElongatedPipe(vec3 p, vec3 s, float t){
    p = abs(p);
    p.x -= s.x-s.y;
    p = max(p, 0.);

    float xz = abs(length(p.xz)-s.y+t)-t;
    float y = abs(p.y)-s.z;
    float m = max(xz,y);
    float e = length(max(vec2(xz,y),0.));
    return min(m,0.)+e;
}

float sdRectPipe(vec3 p, vec2 s, float h, float t){
    vec2 d = abs(p.xz)-s;
    float xz = length(max(d,0.)) + min(max(d.x,d.y),0.) + t;
    xz = abs(xz)-t;
    float y = abs(p.y)-h;
    float m = max(xz,y);
    float e = length(max(vec2(xz,y),0.));
    return min(m,0.)+e;
}

float map( in vec3 p )
{
    float el = abs(sin(iTime))*.75;
    float tk = max(0.,cos(iTime))*.25+.1;
    float rn = .05;//abs(sin(iTime*.25))*.05+.025;
    float d = 3e38;
    d = min(d, sdCylinder(p+vec2(3,0).yyx, .5, .5));
    d = min(d, sdPipe(p+vec2(1.5,0).yyx, .5, .5, tk));
    d = min(d, sdRectPipe(p+vec2(-3,0).yyx, vec2(.5+el, .5), .5, tk));

    d = min(d, sdElongatedCylinder(p+vec2(0,0).yyx, vec3(.5+el, .5, .5)));
    d = min(d, sdElongatedPipe(p+vec2(-1.5,0).yyx, vec3(.5+el, .5, .5), tk));
    return d-rn;
}

