// Shows how to use the mouse input (only left button supported):
//
//      mouse.xy  = mouse position during last button down
//  abs(mouse.zw) = mouse position during last button click
// sign(mouze.z)  = button is down
// sign(mouze.w)  = button is clicked

const int K_NUMP_0 = 96;
const int K_NUMP_1 = 97;
const int K_NUMP_2 = 98;
const int K_NUMP_3 = 99;
const int K_NUMP_4 = 100;
const int K_NUMP_5 = 101;
const int K_NUMP_6 = 102;
const int K_NUMP_7 = 103;
const int K_NUMP_8 = 104;
const int K_NUMP_9 = 105;

#define ID(c) (distance(C, c)<1.)
#define getState(k)    ((texelFetch( iChannel0, ivec2(k, 0), 0 ).x)>.5)
#define getKeypress(k) ((texelFetch( iChannel0, ivec2(k, 1), 0 ).x)>.5)
#define getToggle(k)   ((texelFetch( iChannel0, ivec2(k, 2), 0 ).x)>.5)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame < 2 ) {
        fragColor = vec4(0);
        return;
    }

    vec2 C = fragCoord;
    vec4 last = texelFetch(iChannel1, ivec2(C), 0);

    if(ID(C_MOUSE)){

        // Mouse
        if(sign(iMouse.z)>.0)
            fragColor = iMouse;
        else
            fragColor = last;
        return;

    }
    else
    if(ID(C_CAMERA)){

        // Numpad
        fragColor = last;
        bool isFreeCamera = bool(last.y);

        if(getKeypress(K_NUMP_0) && sign(iMouse.z)<=.0)fragColor = vec4(0, 0, 0, 0);

        //if(getState(K_NUMP_0))fragColor = vec4(0, true, 0, 0);
        if(getState(K_NUMP_1))fragColor = vec4(1, true, 0, 0);
        //if(getState(K_NUMP_2))fragColor = vec4(2, true, 0, 0);
        if(getState(K_NUMP_3))fragColor = vec4(3, true, 0, 0);
        //if(getState(K_NUMP_4))fragColor = vec4(4, true, 0, 0);
        //if(getState(K_NUMP_6))fragColor = vec4(6, true, 0, 0);
        if(getState(K_NUMP_7))fragColor = vec4(7, true, 0, 0);
        //if(getState(K_NUMP_8))fragColor = vec4(8, true, 0, 0);
        //if(getState(K_NUMP_9))fragColor = vec4(9, true, 0, 0);
        return;

    }
    else
    {
        // Blank
        fragColor = vec4(0.0,0.0,1.0,1.0);
    }
}

