float fonts(vec2 p){
    p*=5.0;
    p.x+=iTime*0.5;
    vec2 gv = fract(p)-0.5;
    vec2 id = floor(p);

    float n = Hash21(id)*40.0;

    int char = int(n);
    float size = 0.35;
    float d = drawFont(gv*size,char);
    if(char < 10){
        int num = int(mod(iTime*float(n),10.0));
        d = drawFont(gv*size,num);
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    float d = fonts(uv);

    col = mix(col,vec3(1.),S(d,0.0));

    fragColor = vec4(col,1.0);
}

