#define TURN_TABLE_SPEED .5

float sdBox(vec3 p, vec3 s){
    p = abs(p)-s;
    return max(p.x,max(p.y,p.z));//length(max(p,0.)) + min(0.,max(p.x,max(p.y,p.z)));
}

float sdCylinder(vec3 p, float r, float h){
    return max(length(p.xz)-r, abs(p.y)-h);
}

float opUnionGapped(float a, float b, float g){
    return min(max(a,-b+g),b);
}

float map( in vec3 p )
{
    float sd0 = sdBox(p, vec3(.5));

    vec3 q = abs(p);
    q.xz = rot(q.xz, PI*.25);
    float sd1 = sdBox(q, vec2(.5,.125).xxy);

    float sd2 = sdCylinder(p, .125, .6);

    float gap = .1*(sin(iTime)+1.);

    float d = opUnionGapped(sd0, sd1, gap);
    return opUnionGapped(d, sd2, gap);
}

