// Fork of "water crystals 2" by elenzil. https://shadertoy.com/view/ftVyzK
// 2022-09-07 03:51:17

// Fork of "water crystals 1" by elenzil. https://shadertoy.com/view/7lVyWw
// 2022-08-30 04:23:52

#define RES    (iResolution.xy)
#define MINRES min(RES.x, RES.y)

float wavelengthNM = wl_blu;


// globals
float gT;
const float gMinRayContribution =   0.05;
const uint  gMaxRays            =  10u;
const float gSurfaceEpsilon     =   0.001;

// utils
mat2 rot(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, s, -s, c);
}


struct ray3_t {
    vec3  ro;
    vec3  rd;
    float contribution;
    float side;           // 1 = external -1 = internal
};

// ringbuffer of rays
#define Q_TYPE ray3_t
#define Q_MAX_ENTRIES 10u
Q_IMPLEMENTATION


/////////////////////////////////////////////


// business

struct hit_t {
    bool  hit;
    vec3  pnt;
    vec3  nrm;
    uint  mtl;
    float t;    // distance along ray
    float originSide;
};

struct mtl_t {
    float ior;
    float diffVsTrns; // 0 = diffuse 1 = transparent, modulo reflection

    // reflect            = Schlick()
    // transmit + diffuse = 1 - reflect
    // transmit           = difVsTrans * (1 - reflect)
    // diffuse            = (1 - difVsTrans) * (1 - reflect)
    //                    = 1 - difVsTrans - reflect + difVsTrans * reflect
};

const uint mtl_ball1 = 0u;
const uint mtl_ball2 = 1u;
const uint mtl_ball3 = 2u;
const uint mtl_ball4 = 3u;
const uint mtl_floor = 4u;

const mtl_t materials[] = mtl_t[](
    mtl_t(ior_aerogel, 0.6),
    mtl_t(ior_water  , 1.0),
    mtl_t(ior_diamond, 1.0),
    mtl_t(ior_mirror1, 0.0),
    mtl_t(ior_shiny1 , 0.0)
);


vec3 gDebugColor = v0;

void rayVsSphere(in ray3_t ray, in vec3 c, float r, uint mtl, inout hit_t hit) {

    vec3 ro = ray.ro - c;
    vec3 rd = ray.rd;

    float A = dot(rd, rd);
    float B = dot(ro, rd) * 2.0;
    float C = dot(ro, ro) - (r * r);

    vec2 roots;
    if (!solveQuadratic(vec3(A, B, C), roots)) {
        return;
    }

    if (roots.y < 0.0) {
        // both hits are before the start of the ray
        return;
    }

    if (roots.x > 0.0) {
        if (roots.x > hit.t) {
            return;
        }
        // good hit
        hit.hit = true;
        hit.t   = roots.x;
        hit.mtl = mtl;
        hit.pnt = ray.ro + hit.t * ray.rd;
        hit.nrm = (hit.pnt - c) / r;
        return;
    }

    hit.originSide = -1.0;

    if (roots.y < hit.t) {
        // good hit
        hit.hit = true;
        hit.t   = roots.y;
        hit.mtl = mtl;
        hit.pnt = ray.ro + hit.t * ray.rd;
        hit.nrm = (hit.pnt - c) / r;
        return;
    }
}

// from https://www.cs.princeton.edu/courses/archive/fall00/cs426/lectures/raycast/sld017.htm
void rayVsPlane(in ray3_t ray, in vec3 n, in float d, uint mtl, inout hit_t hit) {
    float t = -(dot(ray.ro, n) + d) / (dot(ray.rd, n));
    if (t > 0.0 && t < hit.t) {
        hit.hit = true;
        hit.t   = t;
        hit.mtl = mtl;
        hit.pnt = ray.ro + hit.t * ray.rd;
        hit.nrm = n;
    }    
}

vec2 ballPosXY(in float t) {
    return vec2(cos(t * 3.0) * 3.0, sin(t * 6.0)) * 1.5;
}

// hit normal is oriented towards "outside"
hit_t rayVsScene(in ray3_t ray) {
    hit_t hit;
    hit.t   = 1e9;
    hit.hit = false;
    hit.originSide = 1.0;

    float bR = 0.3;
    vec3  bP = vec3(ballPosXY(gT), bR + 0.001).xzy;

    float bt1 = fract(gT * 1.02 + 0.5) * 2.0 - 1.0;
    float bt2 = fract(gT * 3.0 / pi  ) * 2.0 - 1.0;
    float bt3 = fract(gT * 1.18 + 0.5) * 2.0 - 1.0;

    rayVsSphere(ray, -vX * 2.3 + vY * (2.0 - bt1 * bt1), 1.0, mtl_ball1 , hit);
    rayVsSphere(ray,  vX * 0.0 + vY * (2.0 - bt2 * bt2), 1.0, mtl_ball2 , hit);
    rayVsSphere(ray,  vX * 2.3 + vY * (2.0 - bt3 * bt3), 1.0, mtl_ball3 , hit);
    rayVsSphere(ray,  bP                               , bR , mtl_ball4 , hit);
    rayVsPlane (ray,  vY                               , 0.0, mtl_floor, hit);

    return hit;
}

float pointSide(in vec3 p) {
    ray3_t r;
    r.ro = p;
    r.rd = vY;
    hit_t hit = rayVsScene(r);
    return hit.originSide;
}


vec3 gSunDir;

vec3 sky(ray3_t ray) {
    return simple_sky(-vY, ray.rd, -gSunDir);
}

void mainImage(out vec4 RGBA, in vec2 XY) {

    gT = iTime * 0.3;

    float zoom   = 3.0;
    vec2  xy     = vec2(XY - RES/2.0) / MINRES * 2.0 / zoom;

    vec2  m      = dot(iMouse.xy, iMouse.xy) > 2000.0 ? iMouse.xy : iResolution.xy * 0.5;
    vec2  mn     = (m - iResolution.xy/2.0) / iResolution.xy * 2.0;

    vec3 camPos;
    camPos.y     = max(0.05, 3.1 - mn.y * 6.0 + 2.0 * sin(gT * 0.231));
    camPos.xz    = vec2(sin(-gT - mn.x * pi * 1.1), cos(-gT - mn.x * pi * 1.1)) * 6.0;
    vec3  trgPos = vec3(0.0, 1.4, 0.0);

    vec3 otherTrgPos = vec3(ballPosXY(gT - 0.05), 0.5).xzy;
    vec3 otherCamPos = vec3(ballPosXY(gT + 0.2),  0.5).xzy;
    otherCamPos = otherTrgPos + normalize(otherTrgPos - otherCamPos) * 1.5;
    otherCamPos.y = 0.5;
    float camMix = smoothstep(0.7, 0.9, sin(gT * 0.21));

    camPos = mix(camPos, otherCamPos, camMix);
    trgPos = mix(trgPos, otherTrgPos, camMix);


    vec3  camFw  = normalize(trgPos - camPos);
    vec3  camRt  = normalize(cross(camFw, vY) - camMix * vY * camPos.x * 0.05);
    vec3  camUp  = cross(camRt, camFw);
    vec3  rd     = normalize(camFw + camRt * xy.x + camUp * xy.y);

    float lt     = gT * 0.231 - 2.0;
    gSunDir      = normalize(vec3(cos(lt) * 20.0, -3.0, sin(lt) * 4.0));

    vec3  col    = vec3(0.0);

    ray3_t r0;
    r0.ro           = camPos;
    r0.rd           = rd;
    r0.contribution = 1.0;
    r0.side         = pointSide(camPos);

    float invWavelengthMicrometersSquared = 1.0 / (wavelengthNM * wavelengthNM * 1e-6);

    QEnqueue(r0);


    uint rayCount = 0u;

    while (!QIsEmpty()) {
        ray3_t ray = QDequeue();

        hit_t hit = rayVsScene(ray);

        if (!hit.hit) {
            col += ray.contribution * sky(ray);
            continue;
        }

        vec3 hitFacingNormal = hit.nrm * ray.side;

        mtl_t mtl     = materials[hit.mtl];
        float ior     = cauchyN(invWavelengthMicrometersSquared, mtl.ior);
        float R0      = schlickR0(ior, ior_air);
        float reflAmt = schlick  (R0, dot(ray.rd, -hitFacingNormal));
        float trnsAmt = mtl.diffVsTrns * (1.0 - reflAmt);
        float diffAmt = (1.0 - mtl.diffVsTrns) * (1.0 - reflAmt);

        reflAmt *= ray.contribution;
        trnsAmt *= ray.contribution;
        diffAmt *= ray.contribution;


        if (diffAmt > gMinRayContribution) {

            vec3 albedo = v1;
            if (hit.mtl == mtl_floor) {
                float q = 1.0;

                q *= smoothstep(-0.05, 0.05, sin((hit.pnt.x - 0.5) * pi));
                q *= smoothstep(-0.05, 0.05, sin((hit.pnt.z - 0.5) * pi));

                albedo *= 0.2 + 0.8 * (1.0 - q);
            }

            // shadow
            ray3_t shadowRay;
            shadowRay.ro = hit.pnt + hit.nrm * 0.01;
            shadowRay.rd = -gSunDir;
            hit_t shadowHit = rayVsScene(shadowRay);
            float lightAmt = shadowHit.hit ? 0.1 : 1.0;

            col += lightAmt * albedo * diffAmt * max(0.0, dot(hit.nrm, -gSunDir));
        }

        bool keepGoing = true;

        keepGoing = keepGoing && (QSpaceLeft() >= 2u);
        keepGoing = keepGoing && (rayCount < gMaxRays);
        keepGoing = keepGoing && ((ray.contribution - diffAmt) > gMinRayContribution);
        if (!keepGoing) {
            continue;
        }

        rayCount += 1u;

        if (mtl.diffVsTrns > 0.001) {
            float eta = ior_air / ior;
            if (ray.side < 0.0) {
                eta = 1.0 / eta;
            }
            vec3 transmtRd       = refract(ray.rd, hitFacingNormal, eta);
            // we don't check total internal because it's not possible
            // for a ray which was originally outside the sphere.
            if (trnsAmt > gMinRayContribution) {
                ray3_t transmtRay        = ray;
                transmtRay.contribution  = trnsAmt;
                transmtRay.rd            = transmtRd;
                transmtRay.side         *= -1.0;
                transmtRay.ro            = hit.pnt - hitFacingNormal * gSurfaceEpsilon;
                QEnqueue(transmtRay);
            }
        }

        if (reflAmt > gMinRayContribution) {
            ray3_t reflectRay;
            reflectRay.contribution  = reflAmt;
            reflectRay.ro            = hit.pnt + hitFacingNormal * gSurfaceEpsilon;
            reflectRay.rd            = reflect(ray.rd, hit.nrm);
            reflectRay.side          = ray.side;
            QEnqueue(reflectRay);
        }
    }

    // boost darker colors
    col = pow(col, vec3(1.0/1.7));

    // col = col + gDebugColor;

    RGBA = vec4(vec3(col), float(rayCount));
}

