// Buffer A updates a compressed cellData per pixel

// Particles die after this many frames
const float MAX_AGE = 400.0;

// Particles are created each frame on each pixel with this probability
const float SPAWN_PROBABILITY = 5e-6;

// Bigger RANGE makes larger velocities possible, but starts to be prohibitively expensive
const int RANGE = 4;

// Control particle rendering into the trail buffer
const float MIN_SIZE = 0.1;
const float MAX_SIZE = 1.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifc = ivec2(fragCoord);
    vec4 state = texelFetch(iChannel0, ivec2(0), 0);

    if (ifc == ivec2(0))
    {
        // Reset if resolution doesn't match
        if (iFrame == 0 || abs(state.xy) != iResolution.xy || keyDown(KEY_SPACE))
        {
            fragColor = vec4(-iResolution.xy, vec2(.5));
        }
        else
        {
            if (iMouse.z > 0.)
            {
                state.zw = iMouse.xy / iResolution.xy;
            }

            fragColor = vec4(abs(state.xy), state.zw);
        }
        return;
    }

    float h = hash(
        ifc.x + 
        ifc.y*int(iResolution.x) + 
        iFrame*int(iResolution.x*iResolution.y)
    );

    if (iFrame == 0 || state.x < 0.)
    {
        // Clear buffer
        cellData init;
        init.pos = vec2(0.5, 0.5);
        init.vel = vec2(0);
        init.density = 0.0;
        init.trailDensity = 0.0;
        init.color = 0.0;
        init.age = 0.0;

        fragColor = packCell(init);

        return;
    }

    if (h > 1. - SPAWN_PROBABILITY)
    {
        // Spawn new particle
        float h1 = hash(
            ifc.x*int(iResolution.x) + 
            ifc.y*int(iResolution.y) + 
            iFrame*int(iResolution.x*iResolution.y)
        );

        float h2 = hash(int(h1*994888228.));
        float h3 = hash(int(h2*4343555.));
        float h4 = hash(int(h3*84999223.));

        cellData newParticle;
        newParticle.pos = vec2(0.5, 0.5); // Start in the middle of the cell
        newParticle.vel = vec2(h1 - 0.5, h2 - 0.5);
        newParticle.density = 1.0;
        newParticle.trailDensity = 1.0;
        newParticle.color = h3;
        newParticle.age = h4;

        fragColor = packCell(newParticle);
    }
    else
    {
        float newDensity = 0.;
        vec2 newVel = vec2(0), newPos = vec2(0);
        cellData d = unpackCell(texelFetch(iChannel0, ivec2(fragCoord), 0));

        // Search the nearest NxN cells to find any that has density that wants to move into our cell
        for (int x = -RANGE; x <= RANGE; ++x)
        {
            for (int y = -RANGE; y <= RANGE; ++y)
            {
                if (ifc.x + x < 0 || ifc.x + x >= int(iResolution.x)) continue;
                if (ifc.y + y < 0 || ifc.y + y >= int(iResolution.y)) continue;

                cellData nb = unpackCell(texelFetch(iChannel0, ivec2(fragCoord) + ivec2(x, y), 0));

                vec2 nbPos = nb.pos + vec2(x, y);
                vec2 newNbPos = nbPos + nb.vel * float(RANGE);

                if (nb.density > 0. && nb.age < 1.0) // Note: also rejecting particles here thar are too old
                {
                    // Draw smooth line from old position to new, even if it jumped past our position
                    float aa = smoothstep(MAX_SIZE, MIN_SIZE, linePointDist(fragCoord + newNbPos, fragCoord + nbPos, fragCoord));

                    // Use full color if we're drawing on black
                    d.color = d.trailDensity < .2 || aa > .2 ? nb.color : d.color;
                    d.trailDensity += aa;

                    if (all(greaterThan(newNbPos, vec2(0))) && all(lessThan(newNbPos, vec2(1))))
                    {
                        newPos = newNbPos;
                        newVel = nb.vel;
                        newDensity = 1.;

                        // Apply some rotation to make the spirals
                        newVel = rot2(0.2*sin(nb.age * 50. * state.w) + nb.age * 0.1 * state.z) * newVel;

                        d.age = nb.age;
                        d.color = nb.color; // Copy over the whole color so it doesnt get lost even with thin lines
                    }
                }
            }
        }

        // Record data in fragColor
        d.pos = newPos;
        d.vel = newVel;
        d.density = min(1., newDensity);
        d.trailDensity *= 0.995; // Fade out old density to keep the display from becoming too busy
        d.age += 1. / MAX_AGE;

        fragColor = packCell(d);
    }
}

common_code:
float pack2(vec2 v)
{
    return uintBitsToFloat(packSnorm2x16(v));
}

vec2 unpack2(float f)
{
    return unpackSnorm2x16(floatBitsToUint(f));
}

struct cellData
{
    vec2 pos;           // 0 ... 1, position within the cell
    vec2 vel;           // -1 ... 1, multiplied by RANGE
    float density;      // either 0 or 1, depending on if a particle is present
    float trailDensity; // 0 ... 1, allows particles to leave trails behind
    float color;        // 0 ... 1, color of trail
    float age;          // 0 ... 1, particles die at age 1
};

cellData unpackCell(vec4 c)
{
    cellData d;

    d.pos = unpack2(c.x);
    d.vel = unpack2(c.y);
    vec2 den = unpack2(c.z);
    d.density = den.x;
    d.trailDensity = den.y;
    vec2 colorAge = unpack2(c.w);
    d.color = colorAge.x;
    d.age = colorAge.y;

    return d;
}

vec4 packCell(cellData d)
{
    float x = pack2(d.pos);
    float y = pack2(d.vel);
    float z = pack2(vec2(d.density, d.trailDensity));
    float w = pack2(vec2(d.color, d.age));

    return vec4(x, y, z, w);
}

//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

mat2 rot2(float a)
{
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

float linePointDist(in vec2 newPos, in vec2 oldPos, in vec2 fragCoord)
{
    vec2 pDelta = (fragCoord - oldPos);
    vec2 delta = newPos - oldPos;
    float deltaLen2 = dot(delta, delta);

    // Find the closest point on the line segment from old to new
    vec2 closest;
    if (deltaLen2 > 0.0000001)
    {
        float deltaInvSqrt = inversesqrt(deltaLen2);
        vec2 deltaNorm = delta * deltaInvSqrt;
        closest = oldPos + deltaNorm * max(0.0, min(1.0 / deltaInvSqrt, dot(deltaNorm, pDelta)));
    }
    else
    {
        // Line is very short; either end will do
        closest = oldPos;
    }

    // Distance to closest point on line segment
    return distance(closest, fragCoord);
}

