    float check = range.x * range.y;
    if (check == 0.) return vec4(vec3(0.), 1e+3);
    vec2 o = p / 16.;
	return textureGrad(iChannel0, o + fract(vec2(c, 15 - c / 16) / 16.), dFdx(o), dFdy(o));
