    float t1 = timeSpan(t, .0, .4);
    float t2 = timeSpan(t, .4, 1.);
    float t3 = timeSpan(t, 1.2, 1.8);
    float t4 = timeSpan(t, 1.8, 2.6);
    float t5 = timeSpan(t, 2.6, 3.8);
    float d = mix(1., d1, t1);
    d = mix(d, d1, t2);
    d = mix(d, d2, t3);
    d = mix(d, d2, t4);
    d = mix(d, 1., t5);

    fragColor = vec4(vec3(smoothstep(.0, .08, abs(d))), 1.);
}

