#define WHI vec3(1., 1., 1.)
#define BLANK vec3(0.35, 0., 0.35)

vec4 getNoise(vec2 uv, float t) {
  float tseed = floor(t*0.1);
  float seed  = tseed;
  float seed2 = tseed + 373.297;
  float seed3 = tseed + 793.713;
  float seed4 = tseed + 127.139;
  float seed5 = tseed + 929.197;
  vec2 roll = -0.05*t*vec2(1.);
  // uv += roll;

  float v = 0.;
  float v1 = 0.;
  float v2 = 0.;
  float v3 = 0.;

  float p4turb = nturb2Simplex(uv*3., seed);
  p4turb = flatten(p4turb, 3.);
  vec2 p4uv = uv/3.0 + p4turb * 0.0025;
  vec2 p4Cel = ncellular(p4uv, seed);
  float p4 = p4Cel.x - p4Cel.y*0.8;
  p4 = normrange(p4, 0.0, 0.1)*2.;
  p4 = sat(p4);

  float p2 = nturbSimplex(uv*5., seed2);
  p2 = flatten(p2, 5.0);
  p2 = p2 * step(0.5, p2);

  float p3 = nfbmSimplex(uv*10., seed);
  float p3b = nfbmSimplex(uv, seed);

  vec2 uvp1 = uv;
  float rotation = PI * 0.25;
  //vec2 dir = vec2(cos(rotation), sin(rotation));
  uvp1 -= cos((uvp1.y*p2)*TWO_PI*50.)*mix(.001, 0.003, flatten(p3b, 2.));
  uvp1 = rotate(uvp1, rotation);
  float baseZoom = 1000.0;
  float xZoom = 1.;
  uvp1 *= baseZoom;
  uvp1.x *= xZoom;
  float rand = random(vec2(floor(uvp1.x), 0.0), seed);
  float yZoom = mix(0.001, .1, rand);
  uvp1.y *= yZoom;
  uvp1 = floor(uvp1);
  float p1 = random(uvp1, seed);
  float mixC = p3;
  float minC = mix(0.05, 0.15, mixC);
  float maxC = mix(0.35, 0.5, mixC);
  float mixB = p3b;
  float minB = mix(.65, 0.8, mixB);
  float maxB = mix(.75, 0.9, mixB);
  p1 = (1.-p4)*mix(minC, maxC, p1) + p4*mix(minB, maxB, p1);

  float final = p1;

  v1 = p1;
  v2 = p4turb;
  v3 = p4;
  v = final;

  return vec4(v1, v2, v3, v);
}


