vec3 withColor(vec4 noises) {
  float v = noises.w;
  float stripes = noises.z;

  // RGB(58,30,18)
  vec3 CMIN = vec3(58., 30., 18.)/255.;

  // RGB(106,62,31)
  //vec3 CMIN = vec3(106., 62., 31.)/255.;

  // RGB(157,124,103)
  //vec3 CMAX = vec3(157., 124., 103.)/255.;
  // RGB(190,143,120)
  //vec3 CMAX = vec3(190.0, 143., 120.)/255.;

  // RGB(126,70,27)
  vec3 CMAX = vec3(126., 70., 27.)*2./255.;

  vec3 c = mix(CMIN, CMAX, normrange(v, 0.0, 0.8));
  //vec3 c = CMAX * normrange(v, 0.0, 0.5);
  c = mix(c, WHI*v, stripes);
  return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from -0.5 to 0.5)
  float mx = max(iResolution.x, iResolution.y);
  vec2 ct = iResolution.xy / mx / 2.0;
  vec2 uv = fragCoord/mx - ct;
  vec2 uv2 = fragCoord / iResolution.xy - .5;

  float t = iTime;
  t *= 0.35;


  int count = 0;
  vec3 sims[30];

  float alldiv = .5;
  vec2 uvZoom1 = uv / 2.;
  vec2 uvZoom2 = uvZoom1 / 2.;
  float txdiv = 1.;
  txdiv *= alldiv;

  vec4 noises1 = getNoise(uv/alldiv, t);
  vec4 noises2 = getNoise(uvZoom1/alldiv, t);
  vec4 noises3 = getNoise(uvZoom2/alldiv, t);

  sims[count++] = WHI * noises1.x;
  sims[count++] = withColor(noises1);
  sims[count++] = WHI * noises1.x;
  sims[count++] = withColor(noises1); //texture(iChannel0, uv/txdiv).rgb;
  sims[count++] = WHI * noises1.y;
  sims[count++] = withColor(noises1);
  sims[count++] = WHI * noises1.y;
  sims[count++] = withColor(noises1); //texture(iChannel0, uvZoom1/txdiv).rgb;
  sims[count++] = WHI * noises1.z;
  sims[count++] = withColor(noises2);
  sims[count++] = WHI * noises1.z;
  sims[count++] = withColor(noises3); //texture(iChannel0, uvZoom2/txdiv).rgb;

  uv2 += .5;
  float gridWidth = 2.0;
  float gridHeight = 6.0;
  float gridX = floor(uv2.x*gridWidth);
  float gridY = floor((1.-uv2.y)*gridHeight);
  int gridInx = int(floor(gridY*gridWidth + gridX));
  vec3 c;
  for (int i = 0; i < count; i++) {
    c += sims[i] * (gridInx == i ? 1.0 : 0.0);
  }


  // Output to screen
  fragColor = vec4(c, 1.0);
}

