#define LEVEL_COUNT 10 //How many levels you want
#define START_COLOR vec3(1.0,0.0,0.0) //level start color
#define END_COLOR  vec3(0.0,1.0,0.0) //level end color
#define RAMP_SMOOTH 0.5f //ramp smooth coefficient


float smoothLevels(float x,float levelCount,float rampSmooth){
    float _step = floor(x * levelCount);
    float _smoothStep = smoothstep(_step,_step + rampSmooth,x*levelCount) + _step;
    return  _smoothStep/levelCount;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float _normalizedSmoothStep = smoothLevels(uv.x,float(LEVEL_COUNT),RAMP_SMOOTH);
    vec3 col = mix(START_COLOR,END_COLOR,_normalizedSmoothStep);
    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float sun = 0.2 / dot(uv, uv);

    vec2 znew,z = vec2(0.0, 0.0); 
    float exponent = 40.0;
    float n = 30.0;
    for(float i = 0.0; i < n; i++)
    {
        znew = vec2(cos(atan(z.y / z.x) * exponent * (fract(iTime * 0.25) + 0.25)) * pow(length(z), 4.0), sin(atan(z.y / z.x) * exponent) * pow(length(z), 4.0)) + uv;
        if(length(znew) > 4.0)
        {
            float result = i / n;
            fragColor = vec4(result, result, result, 1.0);
            return;
        }
        z.x = znew.x;
        z.y = znew.y;
    }
    fragColor = vec4(sun, sun, sun, 1.0);
}

vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
    + i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
#define MAX_FBM 8

float fbm(vec2 uv, float baseFreq, vec2 eye, float lodSpeed) {
    float lodLevel = length(eye - uv) * lodSpeed;
    uv *= baseFreq;
    float amplitude = 1.0, rougthness = 1.0;
    float f = 0.0;
    int i = 0;
    float t = 1.0 / lodLevel;
    for (; i < MAX_FBM; ++i) {
        if(float(i) > t) 
            break;
        f += snoise(uv * amplitude) * rougthness;
        amplitude *= 2.0;
        rougthness /= 1.3;
    }
    t = min(t, float(MAX_FBM));
    f += (1.0 - (float(i) - t)) * snoise(uv * amplitude) * rougthness;
    return f;
}


