{
    vec2 uv = (fragCoord.xy - iResolution.xy / 2.0) / iResolution.xx;
    vec2 m = (iMouse.xy - iResolution.xy / 2.0) / iResolution.xx ;
    fragColor.xyz = vec3(fbm(uv, 2.0, m, 1.0) * 0.5 + 0.5);
    fragColor.r += float(step(length(uv - m), 0.01));
    fragColor.gb -= float(step(length(uv - m), 0.01));
}

