// variant of "why sky color is not violet" by FabriceNeyret2. https://shadertoy.com/view/ltSBRt
// perception = int( intensity(f) * coneResponse(f) )
// using very heuristical cone response. ( May try with: https://www.shadertoy.com/view/llKSDz )

#define N 200.            // spectrum sampling rate

#define RGB(f, fc) ( .5 + .5* cos(3.14*clamp(f-(fc),-1.,1.) ) ) // base lobe approximation
#define EQ(f,a) ( abs(f-(a)) < .5/N )

void mainImage( out vec4 O,  vec2 U )
{
    O -= O;
    vec2 R = iResolution.xy;
    U /= R;
    float y = 7.*U.y, a = fract(y), 
          A = iMouse.z>0. ? iMouse.y/R.y : y >4. ? .5 * ( y-4.) / 3. : .05, // aberration amount
          F = 2.*(2.*U.x-1.),
          I, v=0., r=1.;    
    if (a < 6./R.y && y < 5.) return;              // line separator

    for (float f=-2.; f<2.; f+=1./N) {             // --- color spectrum (normalized):
        r = step( abs(F-f*A), .5 );
        I =   y > 3. ?             r               // integrate filter intensity
            : y > 2. ?      f<F  ? 1. : 0.         // sum from IR to F
            : y > 1. ?      f>F  ? 1. : 0.         // sum from F to UV
            :            EQ(f,F) ? 1. : 0. ;       // peak at F ( i.e. show full spectrum )

        v += I;
        O[0] += I * ( RGB(f, -2./3.)*.7 + RGB(f, 2./3.)*.3);  // perceptive integration
        O[1] += I * RGB(f,  0.   );
        O[2] += I * RGB(f,  2./3.);
    }

 // if ( y > 1. ) O *= 2./v;                       // normalization by luminance
    if ( y > 1. ) O /= N;
    O = pow(O,vec4(1./2.2));                       // RGB to sRGB transform
}

