    Random rng = seed(seed(i),0);
    vec2 p = random2(rng);
    //p.x = gaussian(rng, 0.5, 0.5);
    p.x = float(i) / float(FIT_POINTS-1);
    #if 1
    //p.x = gain(p.x, 1.0/2.0); // distribute more samples at the border
    //p.x = p.x*p.x;
    p = p*2.0 - 1.0;
    p.y = p.y * abs(p.x) * 0.0 + target_func(p.x);
    #else
    p = p*2.0 - 1.0;
    #endif
    return p;
}
