void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float minRes = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5 * iResolution.xy) / minRes;

    vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);
    float sum = data.y;
    sum /= data.z;

    float l = 1. - 1. / pow(2., abs(sum));

    vec3 col = vec3(0);
    if(sign(sum) > 0.) col = mix(col, vec3(0.380,0.380,0.839), l);
    else col = mix(col, vec3(1.000,0.855,0.141), 1.-l);
    if(abs(uv.x) > 0.5) col= vec3(0);
    fragColor = vec4(col, 1.0);
}

