void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 data = texelFetch(iChannel0, ivec2(fragCoord), 0);
    float xn = data.x;
    float sum = data.y;
    float n = data.z;

    float minRes = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5 * iResolution.xy) / minRes + .5;
    vec2 pos = uv * 4. - .5 / minRes;

    float r, xn1;
    xn1 = xn;
    int Sl = S.length();
    if(iFrame == 0) xn1 = .5;
    for(int i = 0; i < 1; i++) {
        for(int j = 0; j < Sl; j++) {
            r = pos.yx[S[j]];
            xn1 = r * xn1 * (1. - xn1);
            sum += log(abs(r*(1.-2.*xn1)));
            n += 1.;
        }
    }

    fragColor = vec4(xn1, sum, n, data.w);
}

