vec3 qHit, ltPos, ltAx, cId;
vec2 gSize, cMid, stAngCs;
float dstFar, tCur, tDir, vuHt;
int idObj, stLib;
bool isPano, colImg, doClimb;
const int idFlr = 1, idCol = 2, idRail = 3, idStr = 4, idShlf = 5, idBk = 6, idLt = 7,
   idSeatC = 8, idSeatM = 9;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qr, qs;
  float dMin, d, r, a, s;
  dMin = dstFar;
  p = vec3 (p.xz - cMid, p.y - gSize.y * (cId.y + 0.5)).xzy;
  r = length (p.xz);
  qs = p;
  qs.xz = Rot2D (qs.xz, pi / 6.);
  a = (r > 0.) ? atan (qs.z, - qs.x) / (2. * pi) : 0.;
  qs.xz = Rot2D (qs.xz, 2. * pi * (floor (6. * a + 0.5) / 6.));
  q = p;
  q.y = 0.5 * gSize.y - 0.3 - abs (p.y);
  d = max (q.y, 9.8 - r);
  DMINQ (idFlr);
  if (stLib >= 3) {
    q.y = p.y;
    q.xz = qs.xz;
    s = length (vec2 (q.x, abs (q.z)) - vec2 (-18.5, 5.));
    d = min (s - 0.4, max (s - 0.6, 9.5 - abs (q.y)));
    DMINQ (idCol);
  }
  if (stLib >= 4) {
    qr = p;
    qr.xz = Rot2Cs (qr.xz, - stAngCs);
    q = qr;
    q.xy = Rot2D (q.xy - 0.5 * gSize.y * vec2 (1., -1.), 0.25 * pi);
    d = 0.5 * max (q.y - abs (0.5 - abs (0.5 - fract (q.x / sqrt (2.)))) * sqrt (2.),
       max (abs (q.z) - 2.2, -0.2 - q.y));
    q.xy = qr.xy;
    DMINQ (idStr);
    q = qr;
    q.xy = Rot2D (q.xy, 0.25 * pi);
    q.z = abs (q.z);
    q.yz -= vec2 (-0.3, 1.2);
    d = PrRoundCylDf (q.yzx, 0.1, 0.02, 12.);
    DMINQ (idLt);
  }
  if (stLib >= 5) {
    q = qr;
    d = max (length (vec2 (r - 10.2, abs (q.y + 6.) - 1.3)) - 0.13, 2. - abs (q.z));
    d = min (d, PrRoundCylDf (vec3 (abs (q.xz) - vec2 (10., 2.), q.y + 7.), 0.2, 0.05, 2.8));
    d = min (d, PrRoundBox2Df (vec2 (r - 9.8, q.y - 0.5 * gSize.y * sign (q.y) - 0.05),
       vec2 (0.05, 0.3), 0.05));
    q.xz = Rot2D (q.xz, 2. * pi / 32.);
    a = (r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
    q.xz = Rot2D (q.xz, 2. * pi * (floor (16. * a + 0.5) / 16.));
    d = min (d, PrCylDf ((q - vec3 (-10.2, -7.2, 0.)).xzy, 0.13, 2.5));
    q = qr;
    q.xy = Rot2D (q.xy, 0.25 * pi);
    d = min (d, max (length (vec2 (q.y - 3.8, abs (q.z) - 2.)) - 0.17, abs (qr.x) - 10.));
    q = qr;
    q.x += 20.;
    q.xy = Rot2D (q.xy, 0.25 * pi);
    d = min (d, max (length (vec2 (q.y - 3.8, abs (q.z) - 2.)) - 0.17, abs (qr.x) - 10.));
    DMINQ (idRail);
  }
  if (stLib >= 6) {
    q = p;
    q.y -= -6.7;
    s = 2.5 - abs (qs.z);
    d = max (SmoothMax (PrRoundBox2Df (vec2 (r - 16.8, q.y), vec2 (1.15, 2.95), 0.05), s, 0.05),
       - max (max (abs (r - 16.) - 1.5, abs (abs (q.y) - 1.4) - 1.25), 0.4 + s));
    DMINQ (idShlf);
    d = SmoothMax (PrRoundBox2Df (vec2 (r - 19.3, q.y + 1.8), vec2 (0.7, 1.2), 0.1),
       6.5 - abs (qs.z), 0.2);
    DMINQ (idSeatC);
    d = SmoothMax (PrRoundBox2Df (vec2 (gSize.x * HexVertDist (q.xz / gSize.x) - 3., q.y + 1.8),
       vec2 (1., 1.2), 0.1), 0.2 - gSize.x * HexEdgeDist (q.xz / gSize.x), 0.2);
    DMINQ (idSeatM);
  }
  if (stLib == 7 && q.y > 0. || stLib >= 8) {
    d = max (max (abs (r - 16.8) - 0.7, abs (q.y) - 2.8), 0.2 + s);
    DMINQ (idBk);
  }
  if (stLib >= 2) {
    q = p;
    q.y -= 9.4;
    d = length (vec2 (r - 11., q.y)) - 0.3;
    q.xz = qs.xz;
    q.x -= -17.;
    d = min (d, PrRoundCylDf (q.xzy, 1., 0.02, 0.2));
    DMINQ (idLt);
  }
  return dMin;
}

void SetCConf ()
{
  float a;
  cMid = HexToPix (cId.xz * gSize.x);
  a = (2. * pi / 6.) * floor (6. * Hashfv2 (cId.xz + vec2 (27.1, 37.1)));
  if (doClimb && cId.x == cId.z) a = - pi / 3. - pi * mod (floor (cId.x / 3.), 2.);
  stAngCs = sin (a + pi / 6. + vec2 (0.5 * pi, 0.));
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec4 vri, hv;
  vec3 vf, p, cIdP;
  vec2 edN[3], pM, s;
  float dHit, d, eps;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.y == 0.) rd.y = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = vec4 (gSize.x / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2])),
     1. / rd.y);
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / gSize.x;
  pM = HexToPix (PixToHex (ro.xz / gSize.x));
  cIdP = vec3 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    p = ro + dHit * rd;
    cId = vec3 (PixToHex (p.xz / gSize.x), floor (p.y / gSize.y)).xzy;
    if (cId != cIdP) {
      cIdP = cId;
      SetCConf ();
    }
    hv = vec4 ((vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))),
       gSize.y * (cId.y + step (0., rd.y))) * vri;
    s = vec2 (Minv3 (hv.xyz), dHit + abs (hv.w - p.y * vri.w));
    d = ObjDf (p);
    if (dHit + d < Minv2 (s)) {
      dHit += d;
    } else if (s.x < s.y) {
      dHit = s.x + eps;
      pM += sqrt3 * ((s.x == hv.x) ? edN[0] : ((s.x == hv.y) ? edN[1] : edN[2]));
    } else {
      dHit = s.y + eps;
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjAO (vec3 ro, vec3 rd)
{
  vec3 p, cIdP;
  float ao, d;
  int idObjT;
  idObjT = idObj;
  cIdP = vec3 (-999.);
  ao = 0.;
  for (int j = VAR_ZERO; j < 4; j ++) {
    d = 0.1 + float (j) / 8.;
    p = ro + d * rd;
    cId = vec3 (PixToHex (p.xz / gSize.x), floor (p.y / gSize.y)).xzy;
    if (cId != cIdP) {
      cIdP = cId;
      SetCConf ();
    }
    ao += max (0., d - 3. * ObjDf (p));
  }
  idObj = idObjT;
  return 0.5 + 0.5 * clamp (1. - 0.2 * ao, 0., 1.);
}

vec3 TrackPath (float t)
{
  vec2 r, dr;
  float tt, h, dh, cxm, cxmm;
  h = vuHt;
  if (! doClimb) {
    tt = mod (t, 4.);
    if (tt < 1.) r = mix (vec2 (sqrt3 * 0.5, -0.5), vec2 (sqrt3 * 0.5, 0.5), tt);
    else if (tt < 2.) r = mix (vec2 (sqrt3 * 0.5, 0.5), vec2 (0., 1.), tt - 1.);
    else if (tt < 3.) r = mix (vec2 (0., 1.), vec2 (0., 2.), tt - 2.);
    else r = mix (vec2 (0., 2.), vec2 (sqrt3 * 0.5, 2.5), tt - 3.);
    r.y += 3. * floor (t / 4.);
    r *= gSize.x;
    r.x += 0.01;
  } else {
    r = vec2 (0.01, gSize.x * t - 3. * gSize.x);
    r = Rot2D (r, - pi / 3.);
    cId.xz = PixToHex (r / gSize.x);
    cxm = mod (floor (cId.x / 3.), 2.);
    cxmm = mod (cId.x, 6.);
    if (cId.x == cId.z) {
      dr = r - HexToPix (cId.xz * gSize.x);
      dh = gSize.y * 0.5 * (1. + clamp ((length (dr) * sign (Rot2D (dr, pi / 3.).y) + 5.) / 10.,
         -1., 1.));
      h += (cxm == 0.) ? dh : gSize.y - dh;
      cxmm = abs (cxmm - 2.5);
      h += gSize.y * (step (cxmm, 2.) + step (cxmm, 1.));
    } else {
      cxmm = abs (cxmm - 3.);
      h += gSize.y * (step (cxmm, 2.5) + step (cxmm, 1.5) + step (cxmm, 0.5));
    }
  }
  return vec3 (r, h).xzy;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, ltVec, ltDir, roo, vn;
  vec2 vf;
  float dstObj, ltDist, ltCeil, ao, atten, r, s, a, y, shLab[2];
  bgCol = vec3 (0.05);
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vf = vec2 (0.);
    if (idObj == idFlr || idObj == idShlf || idObj == idBk) {
      qHit.xz = Rot2D (qHit.xz, pi / 6.);
      r = length (qHit.xz);
      a = (r > 0.) ? atan (qHit.z, - qHit.x) / (2. * pi) + 0.5 : 0.;
    }
    ltCeil = 0.;
    if (idObj == idFlr) {
      vf = vec2 (32., 0.2);
      if (vn.y > 0.01) {
        col4 = vec4 (0.6, 0.4, 0.2, 0.1) * (1. - 0.3 * Fbm2 (ro.xz));
        if (stLib >= 4) col4.rgb *= 0.7 + 0.3 * SmoothBump (0.02, 0.98, 0.01, fract (1.1 * r));
      } else if (vn.y < -0.01) {
        col4 = vec4 (vec3 (1.), 0.);
        if (stLib >= 3) ltCeil = (1. - smoothstep (1., 4., length (Rot2D (qHit.xz,
           2. * pi * floor (6. * a + 0.5) / 6.) - vec2 (17., 0.)))) +
           (1. - smoothstep (0., 4., r - 11.));
        else col4 = vec4 (vec3 (0.4), 0.1);
      } else col4 = vec4 (0.6, 0.4, 0.2, 0.1);
    } else if (idObj == idCol) {
      col4 = vec4 (0.7, 0.7, 0.6, 0.05);
      if (abs (vn.y) < 0.1) col4 *= 0.85 + 0.15 * smoothstep (0.08, 0.1, fract (2. * qHit.y));
      vf = vec2 (32., 1.);
    } else if (idObj == idRail) {
      col4 = vec4 (0.7, 0.7, 0.75, 0.2);
      vf = vec2 (32., 0.2);
    } else if (idObj == idStr) {
      if (abs (qHit.z) > 2.) {
        col4 = vec4 (0.5, 0.55, 0.5, 0.);
        vf = vec2 (32., 0.2);
      } else if (vn.y > -0.01) {
        col4 = vec4 (0.5, 0.3, 0.1, 0.05) * (1. - 0.2 * Fbm2 (2. * ro.xz));
        vf = vec2 (32., 0.2);
      } else {
        col4 = vec4 (0.45, 0.45, 0.5, 0.);
        vf = vec2 (32., 0.2);
        ltCeil = 1. - smoothstep (0., 0.8, PrRoundBox2Df (vec2 (qHit.x, abs (qHit.z) - 1.2),
           vec2 (12. / sqrt(2.), 0.05), 0.05));
      }
    } else if (idObj == idShlf) {
      col4 = vec4 (vec3 (0.7, 0.5, 0.2) * (0.5 + 0.5 * Fbm2 ((r > 17.99) ?
         vec2 (256. * a, 0.5 * qHit.y) :
         ((abs (vn.y) < 0.01) ? vec2 (4. * r, 0.5 * qHit.y) : vec2 (32. * a, 4. * r)))), 0.1);
      vf = vec2 (32., 0.1);
      a = fract (12. * a + 0.5) - 0.5;
      if (stLib >= 7 && r > 17.99 && abs (a) < 0.2) {
        shLab[0] = dot (mod (vec2 (55., 45.) + cId.xz, 100.), vec2 (100., 1.));
        shLab[1] = mod (54. + cId.y, 100.);
        if (abs (qHit.y - 2.2) < 0.4) {
          if (ShowInt (vec2 (- 10. * a - 0.5, (qHit.y - 2.)), vec2 (2., 0.5), 4., shLab[0]) != 0.) 
             col4 = vec4 (0., 0.6, 0.8, -1.);
        } else if (abs (qHit.y - 1.4) < 0.4) {
          if (ShowInt (vec2 (- 10. * a - 0.2, (qHit.y - 1.2)), vec2 (1.5, 0.5), 2., shLab[1]) != 0.)
             col4 = vec4 (0., 0.8, 0.6, -1.);
        }
      }
    } else if (idObj == idBk) {
      a *= 300.;
      s = Hashfv2 (vec2 (floor (a), 1. + floor (qHit.y / 2.8)));
      y = fract (qHit.y / 2.8) / (0.9 - 0.3 * s);
      if (y < 1.) {
        a = fract (a);
        col4 = vec4 (HsvToRgb (vec3 (fract (Hashfv3 (cId) + 0.3 * s), 0.7,
           0.7 * (0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.02, a)))), 0.1);
        col4.rgb *= 1. + smoothstep (0.35, 0.45, abs (y - 0.5)) * ((y > 0.5) ? 0.3 : -0.8);
        col4.rgb = mix (mix (col4.rgb, vec3 (0.7, 0.7, 0.3), SmoothBump (0.2, 0.25, 0.01, y)),
           vec3 (0.8, 0.8, 0.2), step (abs (y - 0.5), 0.15) *
           step (abs (a - 0.5), 0.25) * step (0.5, Noisefv2 (cId.xz * vec2 (19., 31.) +
           floor (vec2 (16. * a, 16. * qHit.y)))));
        vn.xz = Rot2D (vn.xz, 0.5 * pi * (a - 0.5));
      } else {
        col4 = vec4 (0.1, 0.04, 0., -1.);
      }
    } else if (idObj == idSeatC) {
      col4 = vec4 (0.5, 0.3, 0.1, 0.05);
      vf = vec2 (32., 1.5);
    } else if (idObj == idSeatM) {
      col4 = vec4 (0.4, 0.2, 0.1, 0.05);
      vf = vec2 (32., 1.);
    } else if (idObj == idLt) {
      col4 = (stLib >= 3) ? vec4 (1., 1., 0.8, -1.) : vec4 (vec3 (0.8), 0.1);
    }
    ltVec = roo + ltPos - ro;
    ltDist = length (ltVec);
    ltVec /= ltDist;
    atten = 1. / (1. + pow (0.005 * ltDist, 1.5));
    if (stLib <= 3 && ! isPano) atten *= 0.05 + 0.95 * smoothstep (0.7, 0.9, dot (ltAx, - ltVec));
    ao = 1.;
    if (stLib >= 2 && idObj != idBk) ao = ObjAO (ro, vn);
    if (stLib == 1) vf = vec2 (256., 2.);
    if (col4.a >= 0.) {
      if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      col = ao * (col4.rgb * (0.1 + 0.4 * max (vn.y, 0.) + 0.5 * max (dot (vn, ltVec), 0.)) +
         col4.a * pow (max (dot (reflect (ltVec, vn), rd), 0.), 32.));
      if (idObj == idBk) col += 0.3 * col4.rgb * (0.6 - 0.4 * dot (normalize (vn.xz),
         normalize (qHit.xz)));
      if (ltCeil > 0.) col += 0.15 * ltCeil * vec3 (1., 1., 0.8);
      col *= atten;
    } else col = atten * col4.rgb * (0.6 + 0.4 * max (- dot (rd, vn), 0.));
    col = clamp (mix (bgCol, col, exp (32. * min (0., 0.7 - dstObj / dstFar))), 0., 1.);
  } else col = bgCol;
  col = clamp (col, 0., 1.);
  if (stLib <= 2) col = vec3 (0.95, 0.95, 1.) * Maxv3 (col);
  if (stLib == 1) col *= (tDir < 0.) ? vec3 (1., 0.2, 0.2) : vec3 (0.2, 1., 0.2);
  if (! colImg) col = vec3 (0.95, 0.95, 1.) * pow (Maxv3 (col), 0.9);
  return col;
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, colT, colF[2], p1, p2, vd;
  vec2 canvas, uv, uvv, um;
  float az, el, asp, zmFac, sr, tc, t, mb, spd, regBdy, nState;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  gSize = vec2 (30., 20.);
  regBdy = (length (uv * vec2 (1. / asp, 1.)) - 1.5 * SmoothBump (0.5, 0.995, 0.005,
     fract ((1./106.) * tCur))) * canvas.y;
  doClimb = (regBdy > 0.);
  if (! doClimb) {
    spd = 0.35;
    vuHt = 10.;
    p1 = TrackPath (spd * tCur + 0.3);
    p2 = TrackPath (spd * tCur - 0.3);
    ro = 0.5 * (p1 + p2);
    vd = p1 - p2;
    az = atan (vd.x, vd.z);
    el = -0.02 * pi;
  } else {
    spd = 0.35;
    vuHt = 8.5;
    ro = TrackPath (spd * tCur);
    az = pi / 3.;
    el = -0.02 * pi;
  }
  colImg = true;
  isPano = false;
  mb = Minv2 (vec2 (mPtr.x, abs (mPtr.y)) + 0.05 * vec2 (1. / asp, 1.) - 0.5);
  if (mPtr.z > 0. && mb > 0.) {
    if (mPtr.y > 0.) isPano = ! isPano;
    else colImg = ! colImg;
  }
  if (mPtr.z > 0. && colImg && ! isPano && mb < 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.45 * pi, 0.45 * pi);
  vuMat = StdVuMat (el, az);
  zmFac = isPano ? 0.3 : 3.;
  ltPos = vuMat * vec3 (0.3, 2., -1.);
  ltAx = vuMat * vec3 (0., 0., 1.);
  dstFar = 12. * gSize.x;
  nState = 15.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi)) / zmFac;
    rd = vuMat * normalize (isPano ? vec3 (2. * sin (uvv.x / asp + vec2 (0., 0.5 * pi)), uvv.y).xzy : 
       vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
#if DO_FADE
    tc = 0.6 * tCur;
    for (int k = VAR_ZERO; k < 2; k ++) {
      t = mod ((tc + float (k)) / nState, 2.);
      tDir = (2. * floor (t) - 1.) * (1. - abs (t - 1.));
      stLib = int (nState) - int (nState * abs (tDir));
      if (k == 0 || stLib < 9) colF[k] = ShowScene (ro, rd);
      else colF[1] = colF[0];
    }
    colT = mix (colF[0], colF[1], smoothstep (0.2, 0.8, fract (tc)));
    colT *= 1. - step (5. * abs (mod ((tc + 0.5) / nState, 2.) - 1.),
       min (1.5, length (uv * vec2 (1. / asp, 1.))));
#else
    stLib = int (nState);
    colT = ShowScene (ro, rd);
#endif
    col += (1. / naa) * colT;
  }
  if (colImg && ! isPano && doClimb && stLib >= 7) {
    um = uv - vec2 (0., -0.96) * vec2 (asp, 1.);
    if (Maxv2 (abs (um) - vec2 (0.1, 0.05)) < 0.) col = mix (mix (col, vec3 (0., 0., 1.), 0.2),
       vec3 (0.8, 0., 0.), ShowInt (um - vec2 (0.06, -0.02), vec2 (0.1, 0.06) * vec2 (asp, 1.), 3.,
       mod (54. + floor (ro.y / gSize.y), 100.)));
  }
  if (mPtr.z > 0. && min (uv.x - asp, abs (uv.y) - 1.) > -0.1)
     col = mix (col, vec3 (1., 1., 0.), 0.3);

  if (regBdy < -1. && abs (regBdy) < 3.) col = vec3 (0.5, 0.5, 0.);
  fragColor = vec4 (col, 1.);
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexVertDist (vec2 p)
{
  p = abs (p);
  return min (length (vec2 (p.x - (sqrt3/2.), p.y - 0.5)), length (vec2 (p.x, p.y - 1.)));
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

float DigSeg (vec2 q)
{
  return step (abs (q.x), 0.12) * step (abs (q.y), 0.6);
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.5, 2.2);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log (val) / log (10.), 0.) + 0.001) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar == mxChar - nDig - 1. && sgn < 0.) s = ShowDig (q, -1);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

