//smooth min
float smin(float a,float b,float k){
	float h = czo(0.5 + 0.5*(b-a)/k);
	return mix(b,a,h) - k*h*(1.0-h);
}

//smooth max
float smax(float a,float b,float k){
	return -smin(-a,-b,k);
}

struct Phong{
	float amb_iten;
	float diff_iten;
	float spe_iten;
	float spe_alpha;
	float back_iten;
	float diff_absl; //absorbs light
};

struct Obj{
	float f;
	vec3 col;
	vec3 pos;
	int id;
	Phong phong;
};

//primitive 3d sdfs
Obj fSphere(int id,vec3 p,float rad,vec3 col,Phong phong){
	float sph = length(p) - rad;
	return Obj(sph,col,p,id,phong);
}
Obj fEllipsoid(int id,vec3 p,vec3 rad,vec3 col,Phong phong){
  float p1 = length(p/rad);
  float p2 = length(p/sq(rad));
  float elli = p1*(p1-1.0)/p2;
	return Obj(elli,col,p,id,phong);
}
Obj fCube(int id,vec3 p,float size,vec3 col,Phong phong){
	p = abs(p) - size;
	float cube = length(max(p,0.0)) + min(vmax(p),0.0);
	return Obj(cube,col,p,id,phong);
}
Obj fBox(int id,vec3 p,vec3 size,vec3 col,Phong phong){
	p = abs(p) - size;
	float box = length(max(p,0.0)) + min(vmax(p),0.0);
	return Obj(box,col,p,id,phong);
}
Obj fBoxInf1(int id,vec3 p,vec2 size,vec3 col,Phong phong){
	vec2 p2 = abs(p.xy) - size;
	float box = length(max(p2,0.0)) + min(vmax(p2),0.0);
	return Obj(box,col,p,id,phong);
}
Obj fPlane(int id,vec3 p,vec3 n,float d,vec3 col,Phong phong){
	float plane = dot(p,n) - (-d);
	return Obj(plane,col,p,id,phong);
}

//transform matrices
mat3 mRotateX(float theta){
	float s = sin(theta);
	float c = cos(theta);
	return mat3(
		vec3(1,0,0),
		vec3(0,c,-s),
		vec3(0,s,c)
	);
}
mat3 mRotateY(float theta){
	float s = sin(theta);
	float c = cos(theta);
	return mat3(
		vec3(c,0,s),
		vec3(0,1,0),
		vec3(-s,0,c)
	);
}
mat3 mRotateZ(float theta){
	float s = sin(theta);
	float c = cos(theta);
	return mat3(
		vec3(c,-s,0),
		vec3(s,c,0),
		vec3(0,0,1)
	);
}
mat3 mRotateXYZ(float x,float y,float z){
	return mRotateX(x) * mRotateY(y) * mRotateZ(z);
}

