Obj fOpUnS(Obj a,Obj b,float k,int id){ //smooth
	float h = czo(0.5+0.5*(b.f-a.f)/k);
	float f = smin(a.f,b.f,k);
  vec3 col = mix(b.col,a.col,h);
  vec3 pos = mix(b.pos,a.pos,h);
  Phong phong = Phong(
  	mix(b.phong.amb_iten,a.phong.amb_iten,h),
  	mix(b.phong.diff_iten,a.phong.diff_iten,h),
  	mix(b.phong.spe_iten,a.phong.spe_iten,h),
