//texture mappings
vec3 tpmap(sampler2D sam,vec3 p3,vec3 n){ //triplanar
  n = abs(n);
  return (
  	texture(sam,p3.xy) * n.z +
  	texture(sam,p3.xz) * n.y +
  	texture(sam,p3.yz) * n.x
  ).rgb;
}
vec2 cmap(vec3 p3,bool sd){ //cube mapping
  vec3 n = abs(p3);
  vec3 v =
  	(n.x>n.y && n.x>n.z) ? p3.xyz :
    (n.y>n.x && n.y>n.z) ? p3.yzx : p3.zxy;
  vec2 p2 = v.yz/v.x;
  if(sd) p2 *= 1.25 - 0.25*p2*p2;
  return 0.5+0.5*p2;
}
vec2 cmap(vec3 p3){return cmap(p3,false);}
vec2 smap(vec3 p3){ //sphere mapping
  p3 = normalize(p3);
  vec2 p2;
  p2.x = 0.5 + atan(p3.z,p3.x) / tau;
  p2.y = 0.5 - asin(p3.y) / pi;
  return p2;
}
float bumpmap(sampler2D sam,vec3 pos,vec3 n,float dist,float factor,float scale){
	float bump = 0.0;
	if(dist < 0.1){
	  vec3 normal = normalize(n);
	  bump += factor * tpmap(sam,pos*scale,normal).r;
	}
	return bump;
}

