Obj raymarch(vec3 ro,vec3 rd){
	float dist = 0.0;
	Obj hit;

	for(int i=0;i<STEPS;i++){
		vec3 uv3d = ro + dist*rd;

    Obj redl = fSphere(
    	3,uv3d - vec3(2.5,0.1,1.0),0.25,
    	vec3(1.0,0.0,0.0),
    	Phong(1.0,1.0,1.0,10.0,0.1,0.0)
    );
  	Obj lights;
  	lights = redl;
		hit = fOpUn(scene(uv3d),lights);

    dist += hit.f;
		if(abs(hit.f) < EPSILON || dist > MAX_DIST) break;
	}

  hit.f = dist;

	return hit;
}

float softShadow(vec3 ro,vec3 rd){
	float start = MIN_DIST, end = MAX_DIST;
	float k = 16.0;
	float shadow = 1.0;

	for(float d=start;d<end;){
		float hit = scene(ro + d*rd).f;

		if(hit < 0.001) return 0.0;
		shadow = min(shadow,k*hit/d);

        //d += hit / k; //low
		d += hit; //fast
	}

	return shadow;
}

float getAmbOcc(vec3 ro,vec3 rd){
  float occ = 0.0;
  float w = 1.0;

  for(float i=0.0;i<8.0;i++){
  	float len = 0.1+0.01*sq(i);
  	float dist = scene(ro + len*rd).f;
  	occ += (len - dist) * w;
  	w *= 0.775;
  }

  return 1.0 - czo(0.75 * occ);
}

vec3 getNormal(vec3 uv3d){
	vec2 e = vec2(1.0,-1.0) * EPSILON;
	return normalize(vec3(
		e.yyx * scene(uv3d + e.yyx).f +
		e.xyy * scene(uv3d + e.xyy).f +
		e.yxy * scene(uv3d + e.yxy).f +
		e.xxx * scene(uv3d + e.xxx).f
	));
}

struct Light{
	vec3 pos;
	vec3 dir;
	vec3 col;
	vec3 shader;
};
Light light(vec3 uv3d,vec3 rd,vec3 normal,Obj obj,vec3 amb,float iten,vec3 pos,vec3 col){
	vec3 dir = normalize(pos - uv3d);

  switch(obj.id){
  	case 1:
	    float softness = 100.0; //the lower this value, the smoother the texture.
	    vec3 n = pow(abs(normal),vec3(softness));
	    n /= n.x + n.y + n.z;

	    obj.col = tpmap(iChannel0,uv3d,normal).rgb;
  	  break;
	  case 2:
	    obj.col = tpmap(iChannel1,uv3d,normal).rgb;
	    break;
  }

	//shader
	vec3 shader = vec3(0.0);

	if(obj.id == 3){
	  shader += obj.col;
	}else{
    vec3 L = dir;
    vec3 N = normal;
    vec3 R = reflect(-L,N);
    vec3 V = -rd;

    //ambient
    vec3 amb_col = amb*0.05 + obj.col*0.05;
    float amb_iten = obj.phong.amb_iten;
    vec3 ambient = amb_col * amb_iten;

	  //diffuse
  	vec3 diff_col = obj.col * (col * iten*(1.0-obj.phong.diff_absl));
  	float diff_iten = obj.phong.diff_iten;
  	float diff_val = czo(dot(N,L));
  	vec3 diffuse = diff_col * diff_val * diff_iten;

    //specular
    vec3 spe_col = col;
    float spe_iten = obj.phong.spe_iten;
    float spe_alpha = obj.phong.spe_alpha;
    float spe_val = czo(dot(R,V));
    vec3 specular = spe_col * pow(max(0.0,spe_val),spe_alpha) * spe_iten;

    //back
    vec3 b_col = 0.5 * obj.col;
    float b_iten = obj.phong.back_iten;
    float b_val = czo(dot(N,-L));
    vec3 back = b_col * b_val * b_iten;

    //fresnel
    vec3 him_col = obj.col*0.1;
    float him_val = czo(pow(max(0.0,1.0 + dot(rd,N)),2.0));
    vec3 him = him_col * him_val;

    //shadow
    float shadow = 1.0;
    shadow = softShadow(uv3d+N*0.02,normalize(pos));

  	//ambient occlusion
    float occ = getAmbOcc(uv3d,N);

	shader += occ*(ambient+him + ((diffuse*shadow)+back)) + specular;
    //shader += ambient + diffuse + specular;
  }
	return Light(pos,dir,col,shader);
}

mat3 lookAt(vec3 ro,vec3 la){
	vec3 cz = normalize(ro - la);
	vec3 cx = normalize(cross(vec3(0,1,0),cz));
	vec3 cy = normalize(cross(cz,cx));

	return mat3(cx,cy,cz);
}

vec3 render(vec3 col,vec2 uv){
  vec2 mouse = (2.0*iMouse.xy-iResolution.xy)/max(iResolution.x,iResolution.y);

  vec3 bg = vec3(0.1,0.3,1.0);
  vec3 fog = vec3(0.7,0.825,1.0);

  //camera
  float fov = 1.5;
  vec3 la = vec3(0.0,0.0,0.0);
  vec3 ro = vec3(0.0,0.0,2.5);
  ro *= mRotateX(mouse.y - 0.5);
  ro *= mRotateY(-mouse.x * tau * 2.0 + 1.0);
  vec3 rd = lookAt(ro,la) * normalize(vec3(uv,-fov));

  Obj d = raymarch(ro,rd);
  {
    vec3 uv3d = ro + d.f*rd;
    vec3 normal = getNormal(uv3d);

    bool sun_ = false;
    Light sun = light(uv3d,rd,normal,d,bg,1.0,
    	vec3(10.0,35.0,-50.0),
    	vec3(1.0,1.0,0.7)
    );

    Light r = light(uv3d,rd,normal,d,bg,1.0,
    	vec3(2.5,0.1,1.0),
    	vec3(1.0,0.0,0.0)
    );
    Light g = light(uv3d,rd,normal,d,bg,1.0,
    	vec3(-1.0,0.1,1.5),
    	vec3(0.0,1.0,0.0)
    );
    Light b = light(uv3d,rd,normal,d,bg,1.0,
    	vec3(-1.0,0.1,-1.5),
    	vec3(0.0,0.0,1.0)
    );

   if(uv.x <= 0.0) sun_ = true;

   if(d.f < MAX_DIST){
      //shader
      col += r.shader;
      //col += g.shader;
      //col += b.shader;
      //col += r.shader + g.shader + b.shader;

      if(sun_) col += sun.shader;

      //gamma correction
      col = pow(col,vec3(1.0/2.2));

      //fog
      if(!sun_) fog = vec3(-0.0001);
      col = mix(col,fog,1.0-exp(-0.0001*pow(max(0.0,d.f),3.0)));
    }else{
    	//sky
  	  if(sun_) col += mix(bg,fog,1.0-rd.y);
      //sun
      if(rd.x > rd.z && sun_){
        col += sun.col * pow(max(0.0,dot(rd,normalize(sun.pos))),50.0) * 0.2;
        col += sun.col * pow(max(0.0,dot(rd,normalize(sun.pos))),1000.0);
      }
    }
    if(rd.x > rd.z && sun_)
      col += sun.col * pow(max(0.0,dot(rd,normalize(sun.pos))),10.0) * 0.25;
  }

  //buffers ;-;
  col += uv.y * 0.25;
  col += vec3(0.075,0.05,0.1);
  col = pow(col,vec3(1.25));

  return col;
}

vec2 getUV(vec2 c,vec2 offset){
  return (2.0 * (c.xy + offset) - iResolution.xy) / iResolution.y;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
  vec3 col = vec3(0.0);

  if(AA == 1){
    col += render(col,getUV(fragCoord,vec2(0.0)));
  }else if(AA == 2){
    float os1 = float(int(fragCoord.x + fragCoord.y) & 1);
    float os2 = 1.0 - os1;
    col += (
    	render(col,getUV(fragCoord,vec2(0.33 * os1,0.0))) +
    	render(col,getUV(fragCoord,vec2(0.33 * os2,0.66)))
    ) / 2.0;
  }else if(AA == 3){
    float os1 = float(int(fragCoord.x + fragCoord.y) & 1);
    float os2 = 1.0 - os1;
    col += (
    	render(col,getUV(fragCoord,vec2(0.66 * os2,0.0))) +
    	render(col,getUV(fragCoord,vec2(0.66 * os1,0.66))) +
    	render(col,getUV(fragCoord,vec2(0.33,0.33)))
    ) / 3.0;
  }else if(AA == 4){
    vec4 e = vec4(0.125,-0.125,0.375,-0.375);
    col += (
    	render(col,getUV(fragCoord,e.xz)) +
    	render(col,getUV(fragCoord,e.yw)) +
    	render(col,getUV(fragCoord,e.wx)) +
    	render(col,getUV(fragCoord,e.zy))
    ) / 4.0;
  }

  fragColor = vec4(col,1.0);
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

// RayMarching code stolen from TheArtOfCode

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;    
    float a = -0.12 * iTime;
    float zm = 0.65 + 0.5 * thc(4., 0.25 * iTime);
    float r = 2.5 + zm;
    vec3 ro = vec3(r * cos(a), zm, r * sin(a));
    // ro.yz *= rot(-m.y*3.14+1.);
    // ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float GetDist( vec3 p0 ){
    float sd = mlength(p0.xz) - 1.5;
    p0.y += 0.25 + 0.1 * iTime;
    p0 *= 1.5 - 0.75 * thc(4., 0.1 * iTime);
    vec4 p = vec4(p0, 1.);

    // Replace i < 4 with 2, 3, etc.
    for(int i = 0; i < 4; i++){
        // Looks good with i < 3
        // p.xyz = erot(p.xyz, normalize(vec3(1)), float(i) * pi);

        p.xyz = mod(p.yxz-1., 2.)-1.;
        // p.yzx *= exp(-0.015 * length(p));

        // Unsettling motion (best with i < 8 or more)
        // p.y += 0.1 * cos(2. * iTime - pi * p.y);
        p*=(1.235/dot(p.xyz, p.xyz));
    }
    p/=p.w;
    float d1 = abs(p.y);
    float d2 = abs(abs(p.x) - abs(p.z));
    // Intersect + inflate
    float d = 0.1 * smax(d1, d2, 0.005) - 0.001;
    return max(sd, d);
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

// (bad: color not in 0-1 range)
vec3 Bg(vec3 rd) {    
    float k = rd.y * 4.2; 
    vec3 b = pal(abs(rd.y) * -0.2, vec3(0,1,2) / 3.);
    return b * (1.-k);
    //return mix(b, vec3(1.-b),1.-k);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    float zm = 4. - 1. * (0.5 + 0.5 * thc(1., 0.5 * iTime));
    vec3 rd = GetRayDir(uv, ro, vec3(0), zm);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);
    vec3 p = ro + rd * d;
    vec3 n = GetNormal(p);
    vec3 r = reflect(rd, n);
    if(d<MAX_DIST) {   
        // float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        // dif = 4. * dif * (1.-dif);
        // col = vec3(-0.4 + dif);

        float spec = pow(abs(r.y), 16.);
        col += spec;
        col += 0.6 * Bg(r); // * cos(0.1 * p.y);
        col = mix(col, Bg(0.5 * p), 0.8);

        // could do length(p.xz - getRo().xz)
        float fog = 1. / cosh(1.5 * length(p.xz));
        col = mix(Bg(rd), col, fog);

    }
    else col = Bg(r);

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

