#define SCANLLINE_RENDER 8
#define SUNDIR normalize(vec3(2.,.5,1.))

float material;
#define NONE -1.
#define SAND 1.
#define WALL 2.
#define GRASS 3.


// -----------------------------------------------------
// Scene
// -----------------------------------------------------
float ground(vec3 p) { 
    float d = p.y;
    float s = cos(p.x*.05+cos(p.z*.05)+cos(p.x*0.01+p.z*0.02));
    d -= smoothstep(-1.,1.,s)*10.;
    d += noise(p*.1)*5.;
    return d;
}

float walls(vec3 p) {
    float d = -box(p, vec3(100.));
    d = max(d, -box(p-vec3(-100.,-60.,10.), vec3(100.,40.,30.)));
    return d;
}


float grass(vec3 p, float w) {
    float d = abs(box(p-vec3(0.,0.,0.), vec3(100., 103., 100.)));
    d = max(d, p.y+0.);
    //d = max(d,p.y+10.)*3.;
    d -= (noise(p*vec3(.1,0.03,.1))*2.-1.)*5.;
    d -= (noise(p*vec3(.5))*2.-1.)*3.;
    //d = max(d, -w);
    return d;
}


float column(vec3 p) {
    float d = length(p.xz-vec2(-30.,50.))-5.;
    d = max(d, p.y+30.-p.z);
    d = min(d, box(p-vec3(-30.,-100.,50.), vec3(3.,6.,3.))-6.);

    p.xz = rotate(.5) * p.xz;
    float dd = length(p.xy-vec2(-30.,-95))-5.;
    dd = max(dd, p.z);
    dd = max(dd, -p.z+p.x);
    d = min(d, dd);
    return d;

}

float map(vec3 p) {
    float d = ground(p);
    material = SAND;
    float w = walls(p);
    d = max(d, w);
    if (w>-3.) {
        if (w>-5.) 
            material = WALL;
        d -= cos(noise(p*vec3(.1))*8.)*1.;
        d = min(d, column(p));
        d += pow((noise(p*.1)),10.)*5.;
        float g = grass(p,w);
        if (g < d) {
            material = GRASS;
            d = g;
        }
    }
    return d;
}


float mapSimplified(vec3 p) {
    float d = p.y-10.;

    float w = -box(p, vec3(100.));
    w -= 5.;
    d = max(d, w);
    d = min(d, column(p)-1.);
    return d;
}




// -----------------------------------------------------
// Trace ray to the scene
// It's a mix of voxel tracing and SDF tracing 
// -----------------------------------------------------
vec3 trace(vec3 ro, vec3 rd, out vec3 mask){
	vec3 dRd = 1./abs(rd);
	vec3 sRd = sign(rd);


    // Safe start
    vec3 p = floor(ro) + .5;
    vec3 side = dRd*(sRd*(p - ro) + .5);
	for (int i=ZERO; i<16; i++) {
        if (map(p)<0.) return p;
        mask = step(side, side.yzx)*(1. - step(side.zxy, side));
		side += mask*dRd;
		p += mask*sRd;
	}

    // Turbo
    float d = mapSimplified(p);
    if (d>0.) {
        p += rd * d;
        for(int i=0; i<32; i++) {
            d = mapSimplified(p);
            p += rd * d;
            if (d < 1.) break;
        }
        if (p.y>30.) return p;
    }

    // Safe landing
    ro = p;
    p = floor(ro) + .5;
    side = dRd*(sRd*(p - ro) + .5);
	for (int i=0; i<320; i++) {
        if (map(p)<0.) break;
        mask = step(side, side.yzx)*(1. - step(side.zxy, side));
		side += mask*dRd;
		p += mask*sRd;
	}

    return p;    
}

// -----------------------------------------------------
// Shadow stuff
// -----------------------------------------------------
float shadow(vec3 p, vec3 ld) {
    vec3 mask;
    vec3 pp = trace(p,ld,mask);
    return step(0., map(pp));
}
float shadowDistance(vec3 p, vec3 ld) {
    vec3 mask;
    vec3 pp = trace(p,ld,mask);
    return length(pp-p);
}

// -----------------------------------------------------
// Hardcoded blue area light
// -----------------------------------------------------
vec3 sampleAreaLight(vec2 u, vec3 p, vec3 n)
{
    u = u * 2.0f - 1.0f;

    vec3 lightPosition = vec3(-190.,-60.,10.);
    vec3 lightRight = vec3(0.,0.,20.);
    vec3 lightUp = vec3(0.,30.,0.);
    vec3 lightForward = vec3(1.,0.,0.);

    vec3 samplePosition = lightPosition +
                          lightRight * u.x +
                          lightUp * u.y;

    vec3 lightDirection = samplePosition - p;
    float lightDistance = length(lightDirection);
    float inverseLightDistance = 1.0f / max(lightDistance, 1e-3f);
    lightDirection *= inverseLightDistance;

    vec3 col = vec3(0.1,0.9,1.)*50.;
    col *= (inverseLightDistance * inverseLightDistance);
    col *= saturate(dot(-lightDirection, lightForward));
    col *= step(lightDistance, shadowDistance(p+n,lightDirection));

    return col;
}


// -----------------------------------------------------
// Pathtrace
// -----------------------------------------------------
vec3 pathtrace(vec3 ro, vec3 rd, vec3 seed) {

    vec3 abso = vec3(1.);
    vec3 acc = vec3(0.);

    for(int i=0; i<2; i++) {
        vec3 mask;
        vec3 p = trace(ro,rd, mask);
        if (map(p)>0.) {
            break;
        }
        vec3 n = -(mask*sign( rd ));
        float t = length(ro-p);


        vec3 albedo = vec3(.1);
        if (material == SAND)
            albedo = vec3(0.1+(noise(p*.1)*.5+.5)*.1,0.07,0.025);
        else if (material == WALL)
            albedo = vec3(.1,.1,.1);
        else if (material == GRASS) {
            albedo = vec3(.025,.1,.0);
            if (noise(p*.25)>.9 && p.y>-90.) albedo = vec3(.05,.01,.01);
        }

        rd = cosineDirection(seed,n);
        vec3 sd = mix(SUNDIR, seed*2.-1., .05);


        vec3 dcol = vec3(0.);
        dcol += vec3(.5,.7,1.)*.125 * shadow(p+n,rd);
        dcol += vec3(1.,.3,.1)*2. * max(dot(n,sd),0.) * shadow(p+n,sd);
        dcol += sampleAreaLight(vec2(hash(seed+1.),hash(seed)), p, n)*200.;

        abso *= albedo*5.;
        acc += dcol * abso;

        seed = fract(seed + GOLDEN_RATIO);
        ro = p+n;

    }
    return acc;
}

// -----------------------------------------------------
// Entrypoint
// -----------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 invRes = vec2(1.) / iResolution.xy;
    vec2 uv = fragCoord.xy * invRes;
    int f = iFrame % SCANLLINE_RENDER;
    if(abs(uv.x-float(f)/float(SCANLLINE_RENDER)-.5f/float(SCANLLINE_RENDER)) > 1.f/(float(SCANLLINE_RENDER)*2.)) {
        fragColor = texture(iChannel0, uv);
        return;
    }


    material = NONE;

    vec3 seed = hash3(vec3(fragCoord.xy, float(iFrame)));
    vec2 q = (fragCoord.xy+(seed.xy-.5)*.5) * invRes;
    vec2 v = q*2.-1.;
    v.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(91.7, 130., -91.7)*45.5;
    vec3 rd = normalize(vec3(v,100.));
    rd.yz = rotate(PI*.25) * rd.yz;
    rd.xz = rotate(PI*.25) * rd.xz;


    vec3 col = pathtrace(ro,rd, seed);
    vec4 lastFrame = texture(iChannel0, uv);
    fragColor = lastFrame+vec4(col,1.0);

}

