vec3 cosineDirection( vec3 seed, in vec3 n)
{
    vec2 rnd = seed.xy;

    float a = 6.2831853 * rnd.y;
    rnd.x = 2.0*rnd.x - 1.0;
    return normalize( n + vec3(sqrt(1.0-rnd.x*rnd.x) * vec2(cos(a), sin(a)), rnd.x) );

}

