const vec3 eye = vec3(0,0,9);
const vec3 light = vec3(0.0, 3.0, 5.0);
float sphere_move = 1.0;

mat3 rotateX(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1,0,0),
        vec3(0,c,-s),
        vec3(0,s,c)
    );
}

mat3 rotateY(float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c,0,s),
        vec3(0,1,0),
        vec3(-s,0,c)
    );
}


float box ( in vec3 pos, in vec3 size )
{
    vec3 pt = abs ( pos ) - size;

    return length ( max ( pt, 0.0 ) ) + min ( max ( pt.x, max ( pt.y, pt.z ) ), 0.0 );
}

float sphere ( vec3 p, in vec3 center, in float radius )
{
    return length ( p - center ) - radius;
}

float dE1 ( in vec3 p, in mat3 m)
{
    vec3  pt = m * p;
    float d2 = box    ( pt, vec3 ( 0.8, 0.8, 0.8 ) );

    float res = d2;
    for (int i = 0; i <= 1; ++i)
    {
        for (int j = 0; j <= 1; ++j)
        {
            for (int k = 0; k <= 1; ++k)
            {
                float ds = sphere ( pt, vec3 ( -0.8 + float(i) * 1.6, -0.8 + float(j) * 1.6, -0.8 + float(k) * 1.6 ), 0.8 );
                res = max ( -ds, res ); 
            }
        }
    }

    return res;
}

float dE2 ( in vec3 p, in mat3 m)
{
    vec3  pt = m * p;

    float res = 10.0;

    sphere_move = 1.0 + sin(iTime * 1.5) / 5.0;

    for (int i = 0; i <= 1; ++i)
    {
        for (int j = 0; j <= 1; ++j)
        {
            for (int k = 0; k <= 1; ++k)
            {
                float ds = sphere ( pt, vec3 ( -0.8 + float(i) * 1.6, -0.8 + float(j) * 1.6, -0.8 + float(k) * 1.6 ) * sphere_move, 0.5 );
                res = min ( ds, res ); 
            }
        }
    }

    return res;
}

vec3 trace ( vec3 from, vec3 dir, out bool hit, in mat3 m, out int obj)
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

	for ( int steps = 0; steps < 30; steps++ )
	{
		float	dist1 = dE1 ( p, m);
		float	dist2 = dE2 ( p, m);


		if ( min(dist1, dist2) < 0.01 )
		{
			hit = true;
            if (dist1 < 0.01) 
            {
                obj = 1;
            }
            else
            {
                obj = 2;
            }
			break;
		}


		totalDist += min(dist1, dist2);

		if ( totalDist > 10.0 )
			break;

		p += min(dist1, dist2) * dir;
	}

	return p;
}

vec3 generateNormal(vec3 z, in mat3 m)
{
    float e = 0.001;
    float dx1 = min(dE1(z + vec3(e,0,0), m), dE2(z + vec3(e,0,0), m));
    float dx2 = min(dE1(z - vec3(e,0,0), m), dE2(z - vec3(e,0,0), m));
    float dy1 = min(dE1(z + vec3(0,e,0), m), dE2(z + vec3(0,e,0), m));
    float dy2 = min(dE1(z - vec3(0,e,0), m), dE2(z - vec3(0,e,0), m));
    float dz1 = min(dE1(z + vec3(0,0,e), m), dE2(z + vec3(0,0,e), m));
    float dz2 = min(dE1(z - vec3(0,0,e), m), dE2(z - vec3(0,0,e), m));
    return normalize(vec3(dx1 - dx2, dy1 - dy2, dz1 - dz2));
}

vec3 getTriplanarWeights ( in vec3 n ) 
{
	vec3 triW = abs(n);

	return triW / (triW.x + triW.y + triW.z);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool hit;
    int obj;
    vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5, iMouse.z - 0.5);
    mat3 m = rotateX(6.0*mouse.y) * rotateY(6.0*mouse.x);
    vec2 scale = 9.0 * iResolution.xy/max(iResolution.x, iResolution.y);
    vec2 uv = scale * (fragCoord/iResolution.xy - vec2(0.5));
    vec3 dir = normalize(vec3(uv, 0) - eye);
    vec4 color = vec4 (0, 0, 0, 1);
    vec3 p = trace(eye, dir, hit, m, obj);
    vec3 l = normalize(light - p);
    vec3 v = normalize(eye - p);
    vec3 n = generateNormal(p, m);
    float nl = max(0.0, dot(n,l));
    vec3 h = normalize(l + v);
    float hn = max(0.0, dot(h, n));
    float sp = pow(hn, 150.0);
    if (hit)
    {
        color = 0.7 * vec4(nl) + 0.9 * sp * vec4(0.5,0.5,0.5,1);
        vec3 tx = getTriplanarWeights( n );
        vec3 q  = m * p;

        if (obj == 1)
        {
            vec4 cx = texture ( iChannel0, q.yz );
            vec4 cy = texture ( iChannel0, q.zz );
            vec4 cz = texture ( iChannel0, q.xy );
            color *=  tx.x * cx + tx.y * cy + tx.z * cz;
        }
        else
        {
            q  /= sphere_move;
            vec4 cx = texture ( iChannel1, q.yz);
            vec4 cy = texture ( iChannel1, q.zz);
            vec4 cz = texture ( iChannel1, q.xy);
            color *=  tx.x * cx + tx.y * cy + tx.z * cz;
        }
    }

    fragColor = color;
}

